import{_ as e,o as a,c as i,O as s}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 62.1-69.28 Rappahannock River Basin Commission powers.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/62.1-69.28.md","filePath":"vacode/62.1-69.28.md"}'),r={name:"vacode/62.1-69.28.md"},t=s('<h1 id="§-62-1-69-28-rappahannock-river-basin-commission-powers" tabindex="-1">§ 62.1-69.28 Rappahannock River Basin Commission powers. <a class="header-anchor" href="#§-62-1-69-28-rappahannock-river-basin-commission-powers" aria-label="Permalink to &quot;§ 62.1-69.28 Rappahannock River Basin Commission powers.&quot;">​</a></h1><p>A. The Commission shall have no regulatory authority.</p><p>B. To carry out its purposes and mission, the Commission shall have the power to:</p><ol><li><p>Communicate, including through legislative recommendations, Commission views to local, state and federal legislative and administrative bodies, and to others as it deems necessary and appropriate.</p></li><li><p>Undertake studies and prepare, publish and disseminate information in reports and in other forms related to the water quality and natural resources of the Basin and to further its purposes and mission.</p></li><li><p>Enter into contracts and execute all instruments necessary or appropriate.</p></li><li><p>Perform any lawful acts necessary or appropriate.</p></li><li><p>Establish a nonprofit corporation as an instrumentality to assist in the details of administering its affairs and in raising funds.</p></li><li><p>Seek, apply for, accept and expend gifts, grants and donations, services and other aids, from public or private sources. Other than those from member jurisdictions and those appropriated by the General Assembly, funds may be accepted by the Commission only after an affirmative vote by the Commission or by following such other procedure as may be established by the Commission for the conduct of its business.</p></li><li><p>Establish balanced advisory committees that may include representation from agricultural, environmental, resources-based, industrial, recreational, riparian landowner, development, educational and other interests as it deems necessary and appropriate.</p></li><li><p>Develop rules and procedures for the conduct of its business or necessary to carry out its purposes and mission, including, but not limited to, selecting a chair and vice-chairs, rotating chairmanships, calling meetings and establishing voting procedures. Rules and procedures developed pursuant to this subdivision shall be effective upon an affirmative vote by a majority of the Commission members.</p></li></ol><p>1998, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0553" target="_blank" rel="noreferrer">553</a>; 2000, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0386" target="_blank" rel="noreferrer">386</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0456" target="_blank" rel="noreferrer">456</a>.</p>',5),o=[t];function n(l,p,c,d,m,h){return a(),i("div",null,o)}const v=e(r,[["render",n]]);export{f as __pageData,v as default};
