import{_ as e,o as a,c as r,O as n}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 62.1-69.35 Compensation and expenses.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/62.1-69.35.md","filePath":"vacode/62.1-69.35.md"}'),t={name:"vacode/62.1-69.35.md"},o=n('<h1 id="§-62-1-69-35-compensation-and-expenses" tabindex="-1">§ 62.1-69.35 Compensation and expenses. <a class="header-anchor" href="#§-62-1-69-35-compensation-and-expenses" aria-label="Permalink to &quot;§ 62.1-69.35 Compensation and expenses.&quot;">​</a></h1><p>Legislative members of the advisory committee shall receive such compensation as provided in § <a href="http://law.lis.virginia.gov/vacode/30-19.12/" target="_blank" rel="noreferrer">30-19.12</a>, and nonlegislative members shall receive such compensation for the performance of their duties as provided in § <a href="http://law.lis.virginia.gov/vacode/2.2-2813/" target="_blank" rel="noreferrer">2.2-2813</a>. All members shall be reimbursed for all reasonable and necessary expenses incurred in the performance of their duties as provided in §§ <a href="http://law.lis.virginia.gov/vacode/2.2-2813/" target="_blank" rel="noreferrer">2.2-2813</a> and <a href="http://law.lis.virginia.gov/vacode/2.2-2825/" target="_blank" rel="noreferrer">2.2-2825</a>. Funding for the costs of compensation and expenses of members shall be paid from such funds as may be provided to the Department of Environmental Quality in the appropriations act for this purpose.</p><p>2002, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0843" target="_blank" rel="noreferrer">843</a>; 2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0885" target="_blank" rel="noreferrer">885</a>.</p>',3),i=[o];function s(l,p,c,d,h,f){return a(),r("div",null,i)}const v=e(t,[["render",s]]);export{m as __pageData,v as default};
