import{_ as o,o as a,c as t,z as e,a as n}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 62.1-8 Limitation on power of courts to grant leave to erect dams.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/62.1-8.md","filePath":"vacode/62.1-8.md"}'),r={name:"vacode/62.1-8.md"},s=e("h1",{id:"§-62-1-8-limitation-on-power-of-courts-to-grant-leave-to-erect-dams",tabindex:"-1"},[n("§ 62.1-8 Limitation on power of courts to grant leave to erect dams. "),e("a",{class:"header-anchor",href:"#§-62-1-8-limitation-on-power-of-courts-to-grant-leave-to-erect-dams","aria-label":'Permalink to "§ 62.1-8 Limitation on power of courts to grant leave to erect dams."'},"​")],-1),i=e("p",null,"Where a watercourse is navigable, or by law declared a public highway, no court shall grant leave to any person to erect in that part of it any dam which will obstruct ordinary navigation or the passage of fish; and where any law has been or shall be enacted for opening, improving or extending the navigation of a watercourse no court shall, while such law is in force, grant leave to any person to erect any dam or other obstruction across or in such watercourse which will in any way interfere with the navigation thereof without express authority of law or the consent of the company incorporated to open, improve or extend such navigation. Any such dam shall, notwithstanding it may be built under such leave, be deemed a nuisance, and may be abated as such, or such company or the Commonwealth may make a lock or locks in such dam for the passage of vessels and boats without being required to make any compensation therefor.",-1),c=e("p",null,"Code 1950, § 62-6; 1968, c. 659.",-1),h=[s,i,c];function l(d,m,u,p,f,w){return a(),t("div",null,h)}const _=o(r,[["render",l]]);export{v as __pageData,_ as default};
