import{_ as e,o as t,c as r,O as i}from"./chunks/framework.9482e208.js";const b=JSON.parse('{"title":"§ 63.2-1205 Best interests of the child; standards for determining.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/63.2-1205.md","filePath":"vacode/63.2-1205.md"}'),a={name:"vacode/63.2-1205.md"},n=i('<h1 id="§-63-2-1205-best-interests-of-the-child-standards-for-determining" tabindex="-1">§ 63.2-1205 Best interests of the child; standards for determining. <a class="header-anchor" href="#§-63-2-1205-best-interests-of-the-child-standards-for-determining" aria-label="Permalink to &quot;§ 63.2-1205 Best interests of the child; standards for determining.&quot;">​</a></h1><p>In determining whether the valid consent of any person whose consent is required is withheld contrary to the best interests of the child, or is unobtainable, the circuit court or juvenile and domestic relations district court, as the case may be, shall consider whether granting the petition pending before it would be in the best interest of the child. The circuit court or juvenile and domestic relations district court, as the case may be, shall consider all relevant factors, including the birth parent(s)&#39; efforts to obtain or maintain legal and physical custody of the child; whether the birth parent(s) are currently willing and able to assume full custody of the child; whether the birth parent(s)&#39; efforts to assert parental rights were thwarted by other people; the birth parent(s)&#39; ability to care for the child; the age of the child; the quality of any previous relationship between the birth parent(s) and the child and between the birth parent(s) and any other minor children; the duration and suitability of the child&#39;s present custodial environment; and the effect of a change of physical custody on the child.</p><p>1995, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0772" target="_blank" rel="noreferrer">772</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0826" target="_blank" rel="noreferrer">826</a>, § 63.1-225.1; 2000, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0830" target="_blank" rel="noreferrer">830</a>, § 63.1-219.13; 2002, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0747" target="_blank" rel="noreferrer">747</a>; 2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0467" target="_blank" rel="noreferrer">467</a>; 2006, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0825" target="_blank" rel="noreferrer">825</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0848" target="_blank" rel="noreferrer">848</a>.</p>',3),o=[n];function s(h,l,c,d,f,g){return t(),r("div",null,o)}const u=e(a,[["render",s]]);export{b as __pageData,u as default};
