import{_ as t,o as e,c as o,O as a}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 63.2-1220.3 Approval of post-adoption contact and communication agreements.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/63.2-1220.3.md","filePath":"vacode/63.2-1220.3.md"}'),n={name:"vacode/63.2-1220.3.md"},i=a('<h1 id="§-63-2-1220-3-approval-of-post-adoption-contact-and-communication-agreements" tabindex="-1">§ 63.2-1220.3 Approval of post-adoption contact and communication agreements. <a class="header-anchor" href="#§-63-2-1220-3-approval-of-post-adoption-contact-and-communication-agreements" aria-label="Permalink to &quot;§ 63.2-1220.3 Approval of post-adoption contact and communication agreements.&quot;">​</a></h1><p>A. The circuit court may approve a post-adoption contact and communication agreement authorized pursuant to § <a href="http://law.lis.virginia.gov/vacode/16.1-283.1/" target="_blank" rel="noreferrer">16.1-283.1</a> or entered into pursuant to this article and filed with the court for a petition for adoption if:</p><ol><li><p>The court determines that the child&#39;s best interest would be served by approving the post-adoption contact and communication agreement;</p></li><li><p>The adoptive parent or parents and birth parent or parents have consented to a post-adoption contact and communication agreement filed with the court;</p></li><li><p>The agency authorized to place the child for adoption and to consent to an adoption or authorized to recommend the placement of a child for adoption and the child&#39;s guardian ad litem have recommended that the post-adoption contact and communication agreement be approved as being in the best interest of the child, or, if there is no agency sponsoring the adoption, the agency that prepared the adoption report has been informed of the post-adoption contact and communication agreement and has recommended in the agency&#39;s report to the circuit court that the post-adoption contact and communication agreement be approved; however, in cases in which no child placing agency or guardian ad litem for the child is involved, this requirement may be waived; and</p></li><li><p>Where the child is 14 years of age or older, consent to the post-adoption contact and communication agreement is obtained from the child.</p></li></ol><p>B. To be enforceable, any agreement under this section shall be approved by the circuit court and incorporated into the final order of adoption.</p><p>C. The circuit court shall not require execution of a post-adoption contact and communication agreement as a condition for approving any adoption.</p><p>2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0331" target="_blank" rel="noreferrer">331</a>.</p>',6),r=[i];function c(d,p,s,h,m,l){return e(),o("div",null,r)}const g=t(n,[["render",c]]);export{f as __pageData,g as default};
