import{_ as r,o as t,c as i,z as e,a as o}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 63.2-1246.1 Commissioner authority to store, preserve, and certify adoption files.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/63.2-1246.1.md","filePath":"vacode/63.2-1246.1.md"}'),a={name:"vacode/63.2-1246.1.md"},s=e("h1",{id:"§-63-2-1246-1-commissioner-authority-to-store-preserve-and-certify-adoption-files",tabindex:"-1"},[o("§ 63.2-1246.1 Commissioner authority to store, preserve, and certify adoption files. "),e("a",{class:"header-anchor",href:"#§-63-2-1246-1-commissioner-authority-to-store-preserve-and-certify-adoption-files","aria-label":'Permalink to "§ 63.2-1246.1 Commissioner authority to store, preserve, and certify adoption files."'},"​")],-1),n=e("p",null,"Upon receipt of all orders from the clerk of the circuit court and adoption files from the child-placing agency or local board, the Commissioner shall have the authority to direct the storage and preservation of such records. The Commissioner shall have custody of and retain all adoption files, whether in paper or electronic form, including reports, orders, and other documents with identifying information of birth parents and adoptees, in his office or at another location designated by the Commissioner.",-1),d=e("p",null,"The Commissioner or his designee may direct adoption files, in whole or in part, to be microfilmed, digitally reproduced, copied, photographed, or otherwise duplicated for the purpose of preserving and retaining such files. The Commissioner may allow adoption files to be taken from his office or other designated location for the purpose of being microfilmed, digitally reproduced, copied, photographed, or otherwise duplicated, but shall take all necessary and proper precautions, by requiring bonds or otherwise, to ensure the preservation and return and to prevent the mutilation thereof. The Commissioner or his designee shall examine and compare the reproductions from the microfilm, digitally reproduced, copied, photographed, or otherwise duplicated records with the originals and, if satisfied that the copies are exact, certify them as true copies of the records retained by the Commissioner. The same faith and credit shall be given to such reproductions from the microfilm, digitally reproduced, copied, photographed, or otherwise duplicated record as the record reproduced would have been entitled to.",-1),c=e("p",null,[o("2018, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0010",target:"_blank",rel:"noreferrer"},"10"),o(".")],-1),h=[s,n,d,c];function l(p,f,m,u,g,y){return t(),i("div",null,h)}const b=r(a,[["render",l]]);export{v as __pageData,b as default};
