import{_ as e,o as a,c as t,O as r}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 63.2-1248 Fees for home studies, investigations, visitations and reports.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/63.2-1248.md","filePath":"vacode/63.2-1248.md"}'),i={name:"vacode/63.2-1248.md"},o=r('<h1 id="§-63-2-1248-fees-for-home-studies-investigations-visitations-and-reports" tabindex="-1">§ 63.2-1248 Fees for home studies, investigations, visitations and reports. <a class="header-anchor" href="#§-63-2-1248-fees-for-home-studies-investigations-visitations-and-reports" aria-label="Permalink to &quot;§ 63.2-1248 Fees for home studies, investigations, visitations and reports.&quot;">​</a></h1><p>Notwithstanding the provisions of § <a href="http://law.lis.virginia.gov/vacode/17.1-275/" target="_blank" rel="noreferrer">17.1-275</a>, the circuit court with jurisdiction over any adoption matter, or the person, agency, or child-placing agency that attempts to locate the birth family pursuant to § <a href="http://law.lis.virginia.gov/vacode/63.2-1246/" target="_blank" rel="noreferrer">63.2-1246</a> or subsection B of § <a href="http://law.lis.virginia.gov/vacode/63.2-1247/" target="_blank" rel="noreferrer">63.2-1247</a>, or that attempts to locate the adult adoptee pursuant to subsection A of § <a href="http://law.lis.virginia.gov/vacode/63.2-1247/" target="_blank" rel="noreferrer">63.2-1247</a>, shall assess a fee against the petitioner, or applicant and, in the case of local departments, shall assess such fee in accordance with regulations and fee schedules established by the Board, for home studies, investigations, visits and reports provided by the appropriate local department, person, or agency pursuant to §§ <a href="http://law.lis.virginia.gov/vacode/20-160/" target="_blank" rel="noreferrer">20-160</a>, <a href="http://law.lis.virginia.gov/vacode/63.2-1208/" target="_blank" rel="noreferrer">63.2-1208</a>, <a href="http://law.lis.virginia.gov/vacode/63.2-1212/" target="_blank" rel="noreferrer">63.2-1212</a>, <a href="http://law.lis.virginia.gov/vacode/63.2-1231/" target="_blank" rel="noreferrer">63.2-1231</a>, <a href="http://law.lis.virginia.gov/vacode/63.2-1238/" target="_blank" rel="noreferrer">63.2-1238</a> or § <a href="http://law.lis.virginia.gov/vacode/63.2-1246/" target="_blank" rel="noreferrer">63.2-1246</a>. The Board shall adopt regulations and fee schedules, which shall include (i) standards for determining the petitioner&#39;s or applicant&#39;s ability to pay and (ii) a scale of fees based on the petitioner&#39;s or applicant&#39;s income and family size and the actual cost of the services provided. The fee charged shall not exceed the actual cost of the service. The fee shall be paid to the appropriate local department, person, or agency and a receipt therefor shall be provided to the circuit court, or to the Commissioner if pursuant to § <a href="http://law.lis.virginia.gov/vacode/63.2-1246/" target="_blank" rel="noreferrer">63.2-1246</a> or § <a href="http://law.lis.virginia.gov/vacode/63.2-1247/" target="_blank" rel="noreferrer">63.2-1247</a>, prior to the acceptance of parental consent, entry of any final order, or release of identifying information by the Commissioner, and no court shall accept parental consent or enter any final order and the Commissioner shall not release any identifying information until proof of payment of such fees has been received.</p><p>1987, c. 5, § 63.1-236.1; 1989, c. 214; 1990, cc. 101, 297; 1991, c. 600; 1992, c. 607; 1995, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0772" target="_blank" rel="noreferrer">772</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0826" target="_blank" rel="noreferrer">826</a>; 2000, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0830" target="_blank" rel="noreferrer">830</a>, § 63.1-219.55; 2002, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0747" target="_blank" rel="noreferrer">747</a>.</p>',3),n=[o];function s(l,c,h,d,p,f){return a(),t("div",null,n)}const _=e(i,[["render",s]]);export{v as __pageData,_ as default};
