import{_ as e,o as t,c as o,O as a}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 63.2-1516.01 Investigation procedures involving person who is the subject of complaint.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/63.2-1516.01.md","filePath":"vacode/63.2-1516.01.md"}'),n={name:"vacode/63.2-1516.01.md"},i=a('<h1 id="§-63-2-1516-01-investigation-procedures-involving-person-who-is-the-subject-of-complaint" tabindex="-1">§ 63.2-1516.01 Investigation procedures involving person who is the subject of complaint. <a class="header-anchor" href="#§-63-2-1516-01-investigation-procedures-involving-person-who-is-the-subject-of-complaint" aria-label="Permalink to &quot;§ 63.2-1516.01 Investigation procedures involving person who is the subject of complaint.&quot;">​</a></h1><p>The local department shall, at the initial time of contact with the person subject to a child abuse and neglect investigation, advise such person of the complaints or allegations made against the person, in a manner that is consistent with laws protecting the rights of the person making the report or complaint. In cases where a child is alleged to have been abused or neglected by a teacher, principal or other person employed by a local school board or employed in a school operated by the Commonwealth, in the course of such employment in a nonresidential setting, the provisions of § <a href="http://law.lis.virginia.gov/vacode/63.2-1516.1/" target="_blank" rel="noreferrer">63.2-1516.1</a> shall also apply.</p><p>2004, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0093" target="_blank" rel="noreferrer">93</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0233" target="_blank" rel="noreferrer">233</a>.</p>',3),r=[i];function s(l,c,h,p,d,g){return t(),o("div",null,r)}const m=e(n,[["render",s]]);export{v as __pageData,m as default};
