import{_ as e,o as a,c as r,O as i}from"./chunks/framework.9482e208.js";const p=JSON.parse('{"title":"§ 63.2-1600 Home-based services.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/63.2-1600.md","filePath":"vacode/63.2-1600.md"}'),t={name:"vacode/63.2-1600.md"},o=i('<h1 id="§-63-2-1600-home-based-services" tabindex="-1">§ 63.2-1600 Home-based services. <a class="header-anchor" href="#§-63-2-1600-home-based-services" aria-label="Permalink to &quot;§ 63.2-1600 Home-based services.&quot;">​</a></h1><p>Each local board shall provide for the delivery of home-based services that include homemaker, companion, or chore services that will allow individuals to attain or maintain self-care and are likely to prevent or reduce dependency, subject to the supervision and in accordance with regulations of the Commissioner for Aging and Rehabilitative Services as provided in Article 4 (§ <a href="http://law.lis.virginia.gov/vacode/51.5-144/" target="_blank" rel="noreferrer">51.5-144</a> et seq.) of Chapter 14 of Title 51.5. Eligibility for such services shall be determined according to regulations of the Commissioner for Aging and Rehabilitative Services. Such services shall be provided to the extent that federal or state matching funds are made available to each locality.</p><p>1983, c. 605, § 63.1-55.01; 2002, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0747" target="_blank" rel="noreferrer">747</a>; 2012, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0803" target="_blank" rel="noreferrer">803</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0835" target="_blank" rel="noreferrer">835</a>.</p>',3),s=[o];function n(c,l,d,h,v,_){return a(),r("div",null,s)}const g=e(t,[["render",n]]);export{p as __pageData,g as default};
