import{_ as e,o as t,c as i,O as o}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 63.2-1612 Responsibilities of Department; domestic violence prevention and services.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/63.2-1612.md","filePath":"vacode/63.2-1612.md"}'),a={name:"vacode/63.2-1612.md"},n=o('<h1 id="§-63-2-1612-responsibilities-of-department-domestic-violence-prevention-and-services" tabindex="-1">§ 63.2-1612 Responsibilities of Department; domestic violence prevention and services. <a class="header-anchor" href="#§-63-2-1612-responsibilities-of-department-domestic-violence-prevention-and-services" aria-label="Permalink to &quot;§ 63.2-1612 Responsibilities of Department; domestic violence prevention and services.&quot;">​</a></h1><p>It shall be the responsibility of the Department, to the extent funds are appropriated by the General Assembly or otherwise made available:</p><ol><li><p>To support, strengthen, evaluate, and monitor community-based domestic violence programs funded by the Department and to act as the administrator for state grant funds and the disbursal of federal funds pursuant to §§ <a href="http://law.lis.virginia.gov/vacode/63.2-1614/" target="_blank" rel="noreferrer">63.2-1614</a> and <a href="http://law.lis.virginia.gov/vacode/63.2-1615/" target="_blank" rel="noreferrer">63.2-1615</a>;</p></li><li><p>To collaborate with the Statewide Domestic Violence Coalition in developing and implementing community-based programs to respond to and prevent domestic violence;</p></li><li><p>To prepare, disseminate, and present educational programs and materials on domestic violence to the local departments, community provider agencies, and the general public;</p></li><li><p>To support, strengthen, and act as a resource to local departments on issues of domestic violence, particularly as they relate to both adult and child protective services and self-sufficiency;</p></li><li><p>To establish minimum standards of training and provide educational programs to train workers in the fields of child and adult protective services in local departments and community-based domestic violence programs funded by the Department to identify domestic violence and provide effective referrals for appropriate services;</p></li><li><p>To provide training and educational opportunities on effective collaboration for all staff of local departments and community-based domestic violence programs;</p></li><li><p>To work with the Statewide Domestic Violence Coalition to (a) develop policies and procedures that guide the work of persons providing services to victims of domestic violence and their children; (b) implement methods to preserve the confidentiality of all domestic violence services records pursuant to §§ <a href="http://law.lis.virginia.gov/vacode/63.2-104/" target="_blank" rel="noreferrer">63.2-104</a> and <a href="http://law.lis.virginia.gov/vacode/63.2-104.1/" target="_blank" rel="noreferrer">63.2-104.1</a> in order to protect the rights and safety of victims of domestic violence; (c) develop policies and implement methods to assure the confidentiality of records pertaining to the address or location of any shelter or facility assisted under the Family Violence Prevention and Services Act, 42 U.S.C. § 10401 et seq.; (d) collect, prepare, and disseminate statistical data on the occurrence of domestic violence and the services provided throughout the Commonwealth; (e) operate the Virginia Family Violence and Sexual Assault 24-hour toll-free hotline and the Statewide Domestic Violence Database (Vadata); and (f) provide a clearinghouse of information and technical assistance on intervention and prevention of domestic violence;</p></li><li><p>To encourage the use of existing information and referral agencies to provide specialized information on domestic violence;</p></li><li><p>To develop and maintain a statewide list of available community and state resources for the victims of domestic violence;</p></li><li><p>To provide technical assistance on establishing shelters, self-help groups and other necessary service delivery programs;</p></li><li><p>To provide leadership and coordination within the Department on domestic violence as it relates to child and adult abuse and neglect, benefits programs, Temporary Assistance to Needy Families, foster care prevention, child support enforcement, child care, and the promotion of healthy family relationships; and</p></li><li><p>To promote collaboration and cooperation with other state agencies, including the Department of Criminal Justice Services, the Department of Health, the Department of Housing and Community Development, the Office of the Attorney General, and the Virginia Employment Commission, for technical assistance, data collection and service delivery to facilitate the appropriate response to victims of domestic violence.</p></li></ol><p>1980, c. 597, § 63.1-317; 2002, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0747" target="_blank" rel="noreferrer">747</a>; 2005, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0638" target="_blank" rel="noreferrer">638</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0685" target="_blank" rel="noreferrer">685</a>; 2006, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0135" target="_blank" rel="noreferrer">135</a>.</p>',4),r=[n];function s(l,c,d,p,m,h){return t(),i("div",null,r)}const g=e(a,[["render",s]]);export{v as __pageData,g as default};
