import{_ as e,o as t,c as a,O as o}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 63.2-1705.1 (Effective until July 1, 2021) Child day and certain other programs; potable water; lead testing.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/63.2-1705.1.md","filePath":"vacode/63.2-1705.1.md"}'),i={name:"vacode/63.2-1705.1.md"},n=o('<h1 id="§-63-2-1705-1-effective-until-july-1-2021-child-day-and-certain-other-programs-potable-water-lead-testing" tabindex="-1">§ 63.2-1705.1 (Effective until July 1, 2021) Child day and certain other programs; potable water; lead testing. <a class="header-anchor" href="#§-63-2-1705-1-effective-until-july-1-2021-child-day-and-certain-other-programs-potable-water-lead-testing" aria-label="Permalink to &quot;§ 63.2-1705.1 (Effective until July 1, 2021) Child day and certain other programs; potable water; lead testing.&quot;">​</a></h1><p>A. Each child day program that is licensed pursuant to this chapter and any program described in subdivision A 4, B 1, or B 5 of § <a href="/vacode/63.2-1715/">63.2-1715</a> that serves preschool-age children shall develop and implement a plan to test potable water from sources identified by the U.S. Environmental Protection Agency as high priority, including bubbler-style and cooler-style drinking fountains, kitchen taps, classroom combination sinks and drinking fountains, home economics room sinks, teacher&#39;s lounge sinks, nurse&#39;s office sinks, classroom sinks in special education classrooms, and sinks known to be or visibly used for consumption.</p><p>B. The plan established pursuant to subsection A and the results of each test conducted pursuant to such plan shall be submitted to and reviewed by the Commissioner and the Department of Health&#39;s Office of Drinking Water.</p><p>C. If the results of any test conducted in accordance with the plan established pursuant to subsection A indicate a level of lead in the potable water that is at or above 15 parts per billion, the program shall remediate the level of lead in the potable water to below 15 parts per billion and confirm such remediation by retesting the water. The results of the retests shall be submitted to and reviewed by the Commissioner and the Department of Health&#39;s Office of Drinking Water.</p><p>D. Notwithstanding the provisions of subsection A or C, a child day program that is licensed pursuant to this chapter and any program described in subdivision A 4, B 1, or B 5 of § <a href="/vacode/63.2-1715/">63.2-1715</a> may, in lieu of developing and implementing a plan to test potable water or of remediation, use for human consumption, as defined by § <a href="/vacode/32.1-167/">32.1-167</a>, bottled water, water coolers, or other similar water source that meets the U.S. Food and Drug Administration standards for bottled water. Any program that chooses this option shall notify the Commissioner and the Department of Health&#39;s Office of Drinking Water and the parent of each child in the program of such choice.</p><p>2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1084" target="_blank" rel="noreferrer">1084</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1085" target="_blank" rel="noreferrer">1085</a>.</p>',6),r=[n];function s(l,d,c,h,p,f){return t(),a("div",null,r)}const b=e(i,[["render",s]]);export{m as __pageData,b as default};
