import{_ as e,o as t,c as a,O as i}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 63.2-1712 Offenses; penalty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/63.2-1712.md","filePath":"vacode/63.2-1712.md"}'),r={name:"vacode/63.2-1712.md"},n=i('<h1 id="§-63-2-1712-offenses-penalty" tabindex="-1">§ 63.2-1712 Offenses; penalty. <a class="header-anchor" href="#§-63-2-1712-offenses-penalty" aria-label="Permalink to &quot;§ 63.2-1712 Offenses; penalty.&quot;">​</a></h1><p>Any person, and each officer and each member of the governing board of any association or corporation that operates an assisted living facility, adult day care center or child welfare agency, is guilty of a Class 1 misdemeanor if he:</p><ol><li><p>Interferes with any representative of the Commissioner in the discharge of his duties under this subtitle;</p></li><li><p>Makes to the Commissioner or any representative of the Commissioner any report or statement, with respect to the operation of any assisted living facility, adult day care center or child welfare agency, that is known by such person to be false or untrue;</p></li><li><p>Operates or engages in the conduct of an assisted living facility, adult day care center or child welfare agency without first obtaining a license as required by this subtitle or after such license has been revoked or suspended or has expired and not been renewed. No violation shall occur if the facility, center, or agency has applied to the Department for renewal prior to the expiration date of the license. Every day&#39;s violation of this subdivision shall constitute a separate offense; or</p></li><li><p>Operates or engages in the conduct of an assisted living facility, adult day care center, or child welfare agency serving more persons than the maximum stipulated in the license.</p></li></ol><p>Code 1950, §§ 63-227, 63-252; 1954, c. 259; 1968, c. 578, §§ 63.1-182, 63.1-215; 1973, c. 227; 1992, c. 356, § 63.1-194.12; 1993, cc. 957, 993; 2002, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0747" target="_blank" rel="noreferrer">747</a>; 2019, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0449" target="_blank" rel="noreferrer">449</a>.</p>',4),o=[n];function s(c,l,d,f,h,p){return t(),a("div",null,o)}const g=e(r,[["render",s]]);export{_ as __pageData,g as default};
