import{_ as e,o as a,c as r,O as i}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 63.2-1723 Child welfare agencies; criminal conviction and waiver.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/63.2-1723.md","filePath":"vacode/63.2-1723.md"}'),n={name:"vacode/63.2-1723.md"},t=i('<h1 id="§-63-2-1723-child-welfare-agencies-criminal-conviction-and-waiver" tabindex="-1">§ 63.2-1723 Child welfare agencies; criminal conviction and waiver. <a class="header-anchor" href="#§-63-2-1723-child-welfare-agencies-criminal-conviction-and-waiver" aria-label="Permalink to &quot;§ 63.2-1723 Child welfare agencies; criminal conviction and waiver.&quot;">​</a></h1><p>A. Any person who seeks to operate or volunteer or work at a child welfare agency and who is disqualified because of a criminal conviction pursuant to §§ <a href="/vacode/63.2-1720/">63.2-1720</a> and <a href="/vacode/63.2-1721/">63.2-1721</a>, may apply in writing for a waiver from the Commissioner. The Commissioner may grant a waiver if the Commissioner determines that (i) the person is of good moral character and reputation and (ii) the waiver would not adversely affect the safety and well-being of children in the person&#39;s care. The Commissioner shall not grant a waiver to any person who has been convicted of any barrier crime as defined in § <a href="/vacode/19.2-392.02/">19.2-392.02</a>. The child welfare agency shall notify in writing every parent and guardian of the children in its care of any waiver granted for its operators, employees or volunteers.</p><p>B. The Board shall adopt regulations to implement the provisions of this section.</p><p>1998, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0551" target="_blank" rel="noreferrer">551</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0581" target="_blank" rel="noreferrer">581</a>, § 63.1-198.4; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0867" target="_blank" rel="noreferrer">867</a>; 2002, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0747" target="_blank" rel="noreferrer">747</a>; 2015, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0758" target="_blank" rel="noreferrer">758</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0770" target="_blank" rel="noreferrer">770</a>; 2017, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0809" target="_blank" rel="noreferrer">809</a>; 2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0860" target="_blank" rel="noreferrer">860</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0861" target="_blank" rel="noreferrer">861</a>.</p>',4),o=[t];function l(c,s,f,h,g,d){return a(),r("div",null,o)}const _=e(n,[["render",l]]);export{v as __pageData,_ as default};
