import{_ as e,o as i,c as a,O as t}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 63.2-209.1 Office of New Americans.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/63.2-209.1.md","filePath":"vacode/63.2-209.1.md"}'),n={name:"vacode/63.2-209.1.md"},o=t('<h1 id="§-63-2-209-1-office-of-new-americans" tabindex="-1">§ 63.2-209.1 Office of New Americans. <a class="header-anchor" href="#§-63-2-209-1-office-of-new-americans" aria-label="Permalink to &quot;§ 63.2-209.1 Office of New Americans.&quot;">​</a></h1><p>A. There is created in the Department an Office of New Americans (the Office) to assist immigrant integration within the Commonwealth on an economic, social, and cultural level.</p><p>B. The Office shall:</p><ol><li><p>Implement a statewide strategy to promote the economic, linguistic, and civic integration of new Americans in the Commonwealth;</p></li><li><p>Work with localities to coordinate and support local efforts that align with the statewide strategy to promote the economic, linguistic, and civic integration of new Americans in the Commonwealth;</p></li><li><p>Provide advice and assistance to new Americans regarding (i) the citizenship application process and (ii) securing employment, housing, and services for which such persons may be eligible;</p></li><li><p>Provide advice and assistance to state agencies regarding (i) the coordination of relevant policies across state agencies responsible for education, workforce, and training programs, including professional licensure guidance, small business development, worker protection, refugee resettlement, citizenship and voter education or engagement programs, housing programs, and other related programs, and (ii) the dissemination of information to localities and immigration service organizations regarding state programs that help new Americans find and secure employment, housing, and services for which they may be eligible;</p></li><li><p>Educate localities and immigration service organizations on health epidemics and unlawful predatory actions, such as human trafficking, gang recruitment, and fraudulent financial and other schemes, to which communities of such persons may be especially vulnerable;</p></li><li><p>Serve as the primary liaison with external stakeholders, particularly immigrant-serving and refugee-serving organizations and businesses, on immigrant integration priorities and policies;</p></li><li><p>Partner with state agencies and immigrant-serving and refugee-serving organizations and businesses to identify and disseminate beneficial immigrant integration policies and practices throughout the Commonwealth;</p></li><li><p>Manage competitive grant programs that replicate beneficial practices or test new innovations that improve the effectiveness and efficacy of immigrant integration strategies; and</p></li><li><p>Advise the Governor, cabinet members, and the General Assembly on strategies to improve state policies and programs to support the economic, linguistic, and civic integration of new Americans throughout the Commonwealth.</p></li></ol><p>2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1078" target="_blank" rel="noreferrer">1078</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1079" target="_blank" rel="noreferrer">1079</a>.</p>',5),r=[o];function s(c,l,m,p,d,g){return i(),a("div",null,r)}const u=e(n,[["render",s]]);export{f as __pageData,u as default};
