import{_ as e,o as t,c as a,O as i}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 63.2-2102 Powers and duties of the Board of Trustees.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/63.2-2102.md","filePath":"vacode/63.2-2102.md"}'),r={name:"vacode/63.2-2102.md"},n=i('<h1 id="§-63-2-2102-powers-and-duties-of-the-board-of-trustees" tabindex="-1">§ 63.2-2102 Powers and duties of the Board of Trustees. <a class="header-anchor" href="#§-63-2-2102-powers-and-duties-of-the-board-of-trustees" aria-label="Permalink to &quot;§ 63.2-2102 Powers and duties of the Board of Trustees.&quot;">​</a></h1><p>The Board of Trustees shall have the authority to:</p><ol><li><p>Encourage, approve and accept gifts, contributions, bequests, or grants in cash or otherwise from any source, public or private, to carry out the purposes of the Family and Children&#39;s Trust Fund;</p></li><li><p>Administer and disburse any funds available to the Family and Children&#39;s Trust Fund;</p></li><li><p>Engage in fund-raising activities to expand and perpetuate the Family and Children&#39;s Trust Fund;</p></li><li><p>Monitor the use of funds to ensure the accountability of the recipients of funds;</p></li><li><p>Advise the Department, the Board of Social Services, and the Governor on matters concerning programs for the prevention of child abuse and neglect and family violence, the treatment of abused and neglected children and their families, and such other issues related to child abuse and neglect and family violence as identified by the Commissioner;</p></li><li><p>Communicate to the Departments of Behavioral Health and Developmental Services, Corrections, Criminal Justice Services, Education, Health, and Juvenile Justice, other state agencies as appropriate, and the Attorney General activities of the Board of Trustees related to efforts to prevent and treat child abuse and neglect and violence within families;</p></li><li><p>Encourage public awareness activities concerning child abuse and neglect and violence within families;</p></li><li><p>Adopt bylaws and other internal rules for the efficient management of the Family and Children&#39;s Trust Fund; and</p></li><li><p>Administer all matters necessary and convenient to carry out the powers and duties expressly given in this chapter.</p></li></ol><p>1986, c. 416, § 63.1-329; 2002, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0747" target="_blank" rel="noreferrer">747</a>; 2012, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0803" target="_blank" rel="noreferrer">803</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0835" target="_blank" rel="noreferrer">835</a>.</p>',4),o=[n];function s(l,d,c,h,p,u){return t(),a("div",null,o)}const g=e(r,[["render",s]]);export{m as __pageData,g as default};
