import{_ as e,o as a,c as o,O as r}from"./chunks/framework.9482e208.js";const p=JSON.parse('{"title":"§ 63.2-216 Powers and duties of Board in general.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/63.2-216.md","filePath":"vacode/63.2-216.md"}'),t={name:"vacode/63.2-216.md"},i=r('<h1 id="§-63-2-216-powers-and-duties-of-board-in-general" tabindex="-1">§ 63.2-216 Powers and duties of Board in general. <a class="header-anchor" href="#§-63-2-216-powers-and-duties-of-board-in-general" aria-label="Permalink to &quot;§ 63.2-216 Powers and duties of Board in general.&quot;">​</a></h1><p>In addition to such other duties as are assigned to it, the Board shall act in a capacity advisory to the Commissioner, and when requested shall confer and advise with him upon such matters as may arise in the performance of his duties. When requested by the Commissioner, or by the Governor, the Board shall investigate such questions and consider such problems as they, or either of them, may submit and shall report their findings and conclusions. The Board may also initiate investigations and consider problems and make recommendations to the Commissioner or to the Governor, of its own motion.</p><p>Code 1950, § 63-24; 1968, c. 578, § 63.1-24; 1981, c. 21; 1999, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0737" target="_blank" rel="noreferrer">737</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0763" target="_blank" rel="noreferrer">763</a>; 2002, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0747" target="_blank" rel="noreferrer">747</a>.</p>',3),n=[i];function s(d,c,l,h,_,m){return a(),o("div",null,n)}const u=e(t,[["render",s]]);export{p as __pageData,u as default};
