import{_ as e,o as i,c as t,O as n}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 63.2-217.1 Board to amend regulations governing emergency response plans of assisted living facilities.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/63.2-217.1.md","filePath":"vacode/63.2-217.1.md"}'),a={name:"vacode/63.2-217.1.md"},r=n('<h1 id="§-63-2-217-1-board-to-amend-regulations-governing-emergency-response-plans-of-assisted-living-facilities" tabindex="-1">§ 63.2-217.1 Board to amend regulations governing emergency response plans of assisted living facilities. <a class="header-anchor" href="#§-63-2-217-1-board-to-amend-regulations-governing-emergency-response-plans-of-assisted-living-facilities" aria-label="Permalink to &quot;§ 63.2-217.1 Board to amend regulations governing emergency response plans of assisted living facilities.&quot;">​</a></h1><p>A. The Board shall amend its regulations governing emergency preparedness and response plans and temporary emergency electrical power sources of assisted living facilities to require the following:</p><ol><li><p>Any assisted living facility that is equipped with an on-site emergency generator shall (i) include in its emergency preparedness and response plan a description of the emergency generator&#39;s capacity to provide sufficient power for the operation of lighting, ventilation, temperature control, supplied oxygen, and refrigeration and (ii) test such emergency generator monthly and maintain records of such tests; and</p></li><li><p>Any assisted living facility that is not equipped with an on-site emergency generator shall (i) enter into an agreement with a vendor capable of providing the assisted living facility with an emergency generator for the provision of electricity during an interruption of the normal electric power supply; (ii) enter into at least one agreement with a separate vendor capable of providing an emergency generator in the event that the primary vendor is unable to comply with its agreement with the assisted living facility during an emergency; and (iii) have its temporary emergency electrical power source connection tested at the time of installation and every two years thereafter by a contracted vendor and maintain records of such tests.</p></li></ol><p>B. The Department shall provide notice to all licensed assisted living facilities regarding the date by which such assisted living facilities must comply with the regulations promulgated pursuant to this section.</p><p>2019, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0091" target="_blank" rel="noreferrer">91</a>.</p>',5),s=[r];function o(l,c,d,g,p,h){return i(),t("div",null,s)}const v=e(a,[["render",o]]);export{f as __pageData,v as default};
