import{_ as e,o as a,c as r,O as t}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 63.2-218 Board to adopt regulations regarding human research.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/63.2-218.md","filePath":"vacode/63.2-218.md"}'),o={name:"vacode/63.2-218.md"},n=t('<h1 id="§-63-2-218-board-to-adopt-regulations-regarding-human-research" tabindex="-1">§ 63.2-218 Board to adopt regulations regarding human research. <a class="header-anchor" href="#§-63-2-218-board-to-adopt-regulations-regarding-human-research" aria-label="Permalink to &quot;§ 63.2-218 Board to adopt regulations regarding human research.&quot;">​</a></h1><p>The Board shall adopt regulations to effectuate the provisions of Chapter 5.1 (§ <a href="http://law.lis.virginia.gov/vacode/32.1-162.16/" target="_blank" rel="noreferrer">32.1-162.16</a> et seq.) of Title 32.1 for human research, as defined in § <a href="http://law.lis.virginia.gov/vacode/32.1-162.16/" target="_blank" rel="noreferrer">32.1-162.16</a>, to be conducted or authorized by the Department, any agency or facility licensed by the Department, or any local department. The regulations shall require the human research committee to submit to the Governor, the General Assembly, and the Commissioner at least annually a report on the human research projects reviewed and approved by the committee and shall require the committee to report any significant deviations from the proposals as approved.</p><p>1992, c. 603, § 63.1-25.01; 2002, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0747" target="_blank" rel="noreferrer">747</a>.</p>',3),i=[n];function s(d,h,l,c,p,m){return a(),r("div",null,i)}const u=e(o,[["render",s]]);export{g as __pageData,u as default};
