import{_ as e,o as a,c as i,O as t}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 63.2-227 Regional providers; duties.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/63.2-227.md","filePath":"vacode/63.2-227.md"}'),r={name:"vacode/63.2-227.md"},o=t('<h1 id="§-63-2-227-regional-providers-duties" tabindex="-1">§ 63.2-227 Regional providers; duties. <a class="header-anchor" href="#§-63-2-227-regional-providers-duties" aria-label="Permalink to &quot;§ 63.2-227 Regional providers; duties.&quot;">​</a></h1><p>There shall be established a regional system of providers of information and referral services. The Department shall select the regional providers.</p><p>The regional providers shall:</p><ol><li><p>Collect, maintain and disseminate resource data;</p></li><li><p>Provide citizen access to information about resources throughout the Commonwealth;</p></li><li><p>Assist in planning functions by providing selected data to the Department on a regular basis;</p></li><li><p>Provide data to public and private agencies other than the Department on a contractual basis;</p></li><li><p>Cooperate with the state administering agency;</p></li><li><p>Seek funds from available sources;</p></li><li><p>Maintain effective relationships between the system and state and local agencies and public and private organizations; and</p></li><li><p>When feasible and appropriate and within the limits of available funds, establish satellite offices or develop cooperative agreements with local information and referral groups and resource and referral groups that can assist the regional providers in performing their duties and responsibilities.</p></li></ol><p>1984, c. 402, § 63.1-314.6; 1990, c. 915; 2002, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0747" target="_blank" rel="noreferrer">747</a>; 2003, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0054" target="_blank" rel="noreferrer">54</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0075" target="_blank" rel="noreferrer">75</a>.</p>',5),n=[o];function s(l,p,d,c,h,g){return a(),i("div",null,n)}const _=e(r,[["render",s]]);export{u as __pageData,_ as default};
