import{_ as e,o as i,c as a,O as r}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 63.2-405 Provisions for determination of eligibility for medical care and medical assistance; provision of social services; regulations.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/63.2-405.md","filePath":"vacode/63.2-405.md"}'),t={name:"vacode/63.2-405.md"},o=r('<h1 id="§-63-2-405-provisions-for-determination-of-eligibility-for-medical-care-and-medical-assistance-provision-of-social-services-regulations" tabindex="-1">§ 63.2-405 Provisions for determination of eligibility for medical care and medical assistance; provision of social services; regulations. <a class="header-anchor" href="#§-63-2-405-provisions-for-determination-of-eligibility-for-medical-care-and-medical-assistance-provision-of-social-services-regulations" aria-label="Permalink to &quot;§ 63.2-405 Provisions for determination of eligibility for medical care and medical assistance; provision of social services; regulations.&quot;">​</a></h1><p>A. The Commissioner shall, in compliance with the state plan for medical assistance services, applicable regulations of the Board and other state and federal law, provide for the determination of eligibility for medical care and medical assistance and social services required for (i) state participation under Public Law 97 of the 89th Congress of the United States, approved July 30, 1965, as amended, and regulations of the Department of Health and Human Services; and (ii) other state and federal programs. The Commissioner, subject to the state plan for medical assistance services, applicable regulations of the Board and other state and federal law, may establish policies, in the form of guidance documents, necessary to implement such functions, including safeguarding information concerning applicants and recipients. An application for medical assistance services for a person admitted to a State Veterans Care Center located in the Commonwealth may be filed and processed in the jurisdiction where such Care Center is located.</p><p>B. The Commissioner for Aging and Rehabilitative Services shall provide for the determination of eligibility for participation in the Auxiliary Grant Program set forth in Article 9 (§ <a href="http://law.lis.virginia.gov/vacode/51.5-159/" target="_blank" rel="noreferrer">51.5-159</a> et seq.) of Chapter 14 of Title 51.5.</p><p>1970, c. 721, § 63.1-97.1; 1974, cc. 44, 45; 1984, c. 498; 1996, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0511" target="_blank" rel="noreferrer">511</a>; 2002, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0747" target="_blank" rel="noreferrer">747</a>; 2004, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0305" target="_blank" rel="noreferrer">305</a>; 2012, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0803" target="_blank" rel="noreferrer">803</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0835" target="_blank" rel="noreferrer">835</a>.</p>',4),n=[o];function s(l,c,d,f,p,h){return i(),a("div",null,n)}const v=e(t,[["render",s]]);export{m as __pageData,v as default};
