import{_ as o,o as i,c as a,z as e,a as t}from"./chunks/framework.9482e208.js";const g=JSON.parse(`{"title":"§ 63.2-411 Construction and operation of children's residential facilities.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/63.2-411.md","filePath":"vacode/63.2-411.md"}`),n={name:"vacode/63.2-411.md"},r=e("h1",{id:"§-63-2-411-construction-and-operation-of-children-s-residential-facilities",tabindex:"-1"},[t("§ 63.2-411 Construction and operation of children's residential facilities. "),e("a",{class:"header-anchor",href:"#§-63-2-411-construction-and-operation-of-children-s-residential-facilities","aria-label":`Permalink to "§ 63.2-411 Construction and operation of children's residential facilities."`},"​")],-1),s=e("p",null,"Subject to approval by the Governor, a local board is authorized and empowered (i) to operate, construct, purchase, renovate or enlarge children's residential facilities for children who are in the custody of such local board by reason of commitment, voluntary entrustment or temporary detention order or (ii) to contract for such services from other counties or cities operating such facilities or from individuals or private corporations whose facilities are licensed by the appropriate state agency. The cost of maintaining children in such facilities through purchase of service contracts shall be established in accordance with regulations of the Board. Any moneys paid by a local board of a county or city to another county or city for services purchased pursuant to this section shall be applied by that county or city to the establishment and operation of such children's residential facilities. Children's residential facilities established pursuant to the provisions of this section shall meet standards prescribed by the Board.",-1),c=e("p",null,[t("Within the limits of appropriations of state funds, the Department shall reimburse the local board one half the actual cost of the construction, purchase, renovation or enlargement of each such facility. The Commonwealth shall reimburse the local board for administrative costs of operations of such facilities, including the entire reasonable cost of food, medicines, disinfectants, beds and bedding, utilities, equipment and service maintenance, transportation, staff salaries and fringe benefits, insurance and other necessary supplies in accordance with the provisions of § "),e("a",{href:"http://law.lis.virginia.gov/vacode/63.2-401/",target:"_blank",rel:"noreferrer"},"63.2-401"),t(".")],-1),l=e("p",null,"In the event that a local board requests and receives financial assistance for the costs of the local share of the construction, purchase, renovation or operation of children's residential facilities for children who are in the custody of such local board from any source other than reimbursement provided pursuant to this section, the total financial assistance and reimbursement shall not exceed the total cost of construction, purchase, renovation or operations, and such funds shall not be considered state funds.",-1),d=e("p",null,[t("1973, c. 383, § 63.1-56.1; 1974, cc. 44, 45; 1977, c. 571; 1978, c. 293; 2002, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0747",target:"_blank",rel:"noreferrer"},"747"),t(".")],-1),h=[r,s,c,l,d];function f(u,p,m,b,v,_){return i(),a("div",null,h)}const w=o(n,[["render",f]]);export{g as __pageData,w as default};
