import{_ as a,o as e,c as t,O as i}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 63.2-501.1 Application for public assistance; accurate contact information; authorized disclosures.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/63.2-501.1.md","filePath":"vacode/63.2-501.1.md"}'),n={name:"vacode/63.2-501.1.md"},o=i('<h1 id="§-63-2-501-1-application-for-public-assistance-accurate-contact-information-authorized-disclosures" tabindex="-1">§ 63.2-501.1 Application for public assistance; accurate contact information; authorized disclosures. <a class="header-anchor" href="#§-63-2-501-1-application-for-public-assistance-accurate-contact-information-authorized-disclosures" aria-label="Permalink to &quot;§ 63.2-501.1 Application for public assistance; accurate contact information; authorized disclosures.&quot;">​</a></h1><p>A. Every local department shall obtain accurate contact information from each applicant for public assistance, which shall include the best available address and telephone number of the applicant. Local departments shall also obtain alternative contact information, such as the applicant&#39;s email address and cell phone number, and the applicant&#39;s preferred method of contact, including direct mail, email, text message, or phone call.</p><p>B. To the extent required by federal law and regulations, recipients of public assistance shall notify the local department of any change in address or telephone number within 30 days of such change, and the local department shall update any records maintained by the local department to reflect the change in the recipient&#39;s contact information.</p><p>C. Contact information received and maintained by local departments shall be confidential and shall not be disclosed except as required pursuant to § <a href="http://law.lis.virginia.gov/vacode/63.2-102/" target="_blank" rel="noreferrer">63.2-102</a>. However, information related to any application for or receipt of medical assistance services pursuant to § <a href="http://law.lis.virginia.gov/vacode/32.1-325/" target="_blank" rel="noreferrer">32.1-325</a> may be disclosed for purposes directly connected to administration of the state plan for medical assistance services pursuant to § 1902(a)(7) of the Social Security Act.</p><p>2012, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0367" target="_blank" rel="noreferrer">367</a>; 2017, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0472" target="_blank" rel="noreferrer">472</a>.</p>',5),r=[o];function c(s,l,d,p,h,f){return e(),t("div",null,r)}const _=a(n,[["render",c]]);export{m as __pageData,_ as default};
