import{_ as e,o as a,c as r,O as t}from"./chunks/framework.9482e208.js";const p=JSON.parse('{"title":"§ 63.2-525 Payment by Department for legal services.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/63.2-525.md","filePath":"vacode/63.2-525.md"}'),o={name:"vacode/63.2-525.md"},n=t('<h1 id="§-63-2-525-payment-by-department-for-legal-services" tabindex="-1">§ 63.2-525 Payment by Department for legal services. <a class="header-anchor" href="#§-63-2-525-payment-by-department-for-legal-services" aria-label="Permalink to &quot;§ 63.2-525 Payment by Department for legal services.&quot;">​</a></h1><p>Notwithstanding any provision of §§ <a href="http://law.lis.virginia.gov/vacode/2.2-2814/" target="_blank" rel="noreferrer">2.2-2814</a>, <a href="http://law.lis.virginia.gov/vacode/2.2-2815/" target="_blank" rel="noreferrer">2.2-2815</a>, <a href="http://law.lis.virginia.gov/vacode/2.2-2816/" target="_blank" rel="noreferrer">2.2-2816</a>, <a href="http://law.lis.virginia.gov/vacode/2.2-2823/" target="_blank" rel="noreferrer">2.2-2823</a>, <a href="http://law.lis.virginia.gov/vacode/2.2-2824/" target="_blank" rel="noreferrer">2.2-2824</a>, <a href="http://law.lis.virginia.gov/vacode/2.2-2825/" target="_blank" rel="noreferrer">2.2-2825</a> or § <a href="http://law.lis.virginia.gov/vacode/2.2-2826/" target="_blank" rel="noreferrer">2.2-2826</a> to the contrary, whenever there shall be authorized by law an assistant attorney for the Commonwealth and such assistant&#39;s duties consist of the prosecution of public assistance fraud cases pursuant to §§ <a href="http://law.lis.virginia.gov/vacode/18.2-95/" target="_blank" rel="noreferrer">18.2-95</a>, <a href="http://law.lis.virginia.gov/vacode/18.2-96/" target="_blank" rel="noreferrer">18.2-96</a>, <a href="http://law.lis.virginia.gov/vacode/63.2-502/" target="_blank" rel="noreferrer">63.2-502</a>, <a href="http://law.lis.virginia.gov/vacode/63.2-513/" target="_blank" rel="noreferrer">63.2-513</a>, <a href="http://law.lis.virginia.gov/vacode/63.2-522/" target="_blank" rel="noreferrer">63.2-522</a>, <a href="http://law.lis.virginia.gov/vacode/63.2-523/" target="_blank" rel="noreferrer">63.2-523</a> or § <a href="http://law.lis.virginia.gov/vacode/63.2-524/" target="_blank" rel="noreferrer">63.2-524</a>, the Department may, with the consent of the attorney for the Commonwealth of the jurisdiction, contract with the county or city or combination thereof for whom such assistant attorney for the Commonwealth is authorized regarding the duties of such assistant and regarding the payment by the Department of the entire salary, expenses, including secretarial services, and allowances of such assistant, as shall be approved by the Compensation Board, for the entire time devoted to these duties. Any such contract may provide that the county, city, or combination thereof shall pay the entire amount of such salary, expenses, and allowances and that the Department shall reimburse such county or city therefor. The amount of such salary, expenses, and allowances shall be set by the Compensation Board as provided by law.</p><p>1991, c. 5, § 63.1-124.3; 2002, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0747" target="_blank" rel="noreferrer">747</a>.</p>',3),i=[n];function s(l,h,c,f,v,d){return a(),r("div",null,i)}const _=e(o,[["render",s]]);export{p as __pageData,_ as default};
