import{_ as e,o as a,c as t,O as i}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 63.2-527 Notice of earned income tax credit.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/63.2-527.md","filePath":"vacode/63.2-527.md"}'),o={name:"vacode/63.2-527.md"},r=i('<h1 id="§-63-2-527-notice-of-earned-income-tax-credit" tabindex="-1">§ 63.2-527 Notice of earned income tax credit. <a class="header-anchor" href="#§-63-2-527-notice-of-earned-income-tax-credit" aria-label="Permalink to &quot;§ 63.2-527 Notice of earned income tax credit.&quot;">​</a></h1><p>The Department shall provide notice regarding the availability of the federal earned income tax credit authorized in § 32 of the Internal Revenue Code and the state earned income tax credit authorized in subdivision B 2 of § <a href="/vacode/58.1-339.8/">58.1-339.8</a> to all recipients of Temporary Assistance for Needy Families pursuant to Chapter 6 (§ <a href="/vacode/63.2-600/">63.2-600</a> et seq.), SNAP benefits pursuant to § <a href="/vacode/63.2-801/">63.2-801</a>, or medical assistance pursuant to § <a href="/vacode/32.1-325/">32.1-325</a> who had earned income in the prior tax year based on information available through the Virginia Employment Commission and, according to information made available by the Virginia Department of Taxation, either did not file federal or state income taxes or filed taxes and did not claim the federal or state earned income tax credit. Notice shall be distributed to recipients annually and shall include information on the qualifying income levels, the amount of credit available, the process for applying for the credit, and the availability of assistance in applying for the credit.</p><p>2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0086" target="_blank" rel="noreferrer">86</a>; 2016, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0029" target="_blank" rel="noreferrer">29</a>.</p>',3),n=[r];function c(d,l,s,f,h,m){return a(),t("div",null,n)}const u=e(o,[["render",c]]);export{_ as __pageData,u as default};
