import{_ as e,o as i,c as r,O as a}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 63.2-601 Virginia Temporary Assistance for Needy Families Program; goals.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/63.2-601.md","filePath":"vacode/63.2-601.md"}'),t={name:"vacode/63.2-601.md"},o=a('<h1 id="§-63-2-601-virginia-temporary-assistance-for-needy-families-program-goals" tabindex="-1">§ 63.2-601 Virginia Temporary Assistance for Needy Families Program; goals. <a class="header-anchor" href="#§-63-2-601-virginia-temporary-assistance-for-needy-families-program-goals" aria-label="Permalink to &quot;§ 63.2-601 Virginia Temporary Assistance for Needy Families Program; goals.&quot;">​</a></h1><p>The goals of the Temporary Assistance for Needy Families Program are to:</p><ol><li><p>Offer Virginians living in poverty the opportunity to achieve economic independence by removing barriers and disincentives to work and providing positive incentives to work;</p></li><li><p>Provide families living in poverty with the opportunities and work skills necessary for self-sufficiency;</p></li><li><p>Allow families living in poverty to contribute materially to their own self-sufficiency;</p></li><li><p>Set out the responsibilities of and expectations for recipients of public assistance and the government; and</p></li><li><p>Provide families living in poverty with the opportunity to obtain work experience through the Virginia Initiative for Education and Work (VIEW).</p></li></ol><p>None of the provisions of this chapter shall be construed or interpreted to create any rights, causes of action, administrative claims or exemptions to the provisions of the Program, except as specifically provided in §§ <a href="/vacode/63.2-609/">63.2-609</a>, <a href="/vacode/63.2-613/">63.2-613</a>, and <a href="/vacode/63.2-618/">63.2-618</a>.</p><p>The Department of Small Business and Supplier Diversity and the Virginia Employment Commission shall assist the Department in the administration of the Program.</p><p>1994, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0858" target="_blank" rel="noreferrer">858</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0951" target="_blank" rel="noreferrer">951</a>, § 63.1-133.41; 1995, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0450" target="_blank" rel="noreferrer">450</a>; 1996, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0589" target="_blank" rel="noreferrer">589</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0599" target="_blank" rel="noreferrer">599</a>; 2002, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0747" target="_blank" rel="noreferrer">747</a>; 2013, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0482" target="_blank" rel="noreferrer">482</a>; 2019, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0210" target="_blank" rel="noreferrer">210</a>.</p>',6),n=[o];function s(l,p,c,g,f,h){return i(),r("div",null,n)}const m=e(t,[["render",s]]);export{v as __pageData,m as default};
