import{_ as e,o as i,c as a,O as t}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 63.2-613 Hardship exceptions.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/63.2-613.md","filePath":"vacode/63.2-613.md"}'),r={name:"vacode/63.2-613.md"},n=t('<h1 id="§-63-2-613-hardship-exceptions" tabindex="-1">§ 63.2-613 Hardship exceptions. <a class="header-anchor" href="#§-63-2-613-hardship-exceptions" aria-label="Permalink to &quot;§ 63.2-613 Hardship exceptions.&quot;">​</a></h1><p>The Board shall adopt regulations providing exceptions to the time limitations of this chapter in cases of hardship. In adopting regulations, the Board shall address circumstances:</p><ol><li><p>Where a VIEW participant has been actively seeking employment by engaging in job-seeking activities required pursuant to § <a href="/vacode/60.2-612/">60.2-612</a> and is unable to find employment;</p></li><li><p>Where factors relating to job availability may be unfavorable;</p></li><li><p>Where the VIEW participant loses his job as a result of factors not related to his job performance; and</p></li><li><p>Where extension of benefits for up to one year will enable a participant to complete employment-related education or training.</p></li></ol><p>The Department shall (i) keep records of the number of VIEW participants who receive an exception to the time limitations on TANF benefits due to hardship and the specific circumstances relied upon to grant such exceptions and (ii) annually publish nonidentifying statistics regarding such information.</p><p>1994, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0858" target="_blank" rel="noreferrer">858</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0951" target="_blank" rel="noreferrer">951</a>, § 63.1-133.51; 1995, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0450" target="_blank" rel="noreferrer">450</a>; 2002, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0747" target="_blank" rel="noreferrer">747</a>; 2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0007" target="_blank" rel="noreferrer">7</a>.</p>',5),o=[n];function s(l,p,c,h,d,f){return i(),a("div",null,o)}const u=e(r,[["render",s]]);export{_ as __pageData,u as default};
