import{_ as e,o as a,c as t,O as i}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 63.2-618 Notice and appeal.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/63.2-618.md","filePath":"vacode/63.2-618.md"}'),r={name:"vacode/63.2-618.md"},n=i('<h1 id="§-63-2-618-notice-and-appeal" tabindex="-1">§ 63.2-618 Notice and appeal. <a class="header-anchor" href="#§-63-2-618-notice-and-appeal" aria-label="Permalink to &quot;§ 63.2-618 Notice and appeal.&quot;">​</a></h1><p>A participant aggrieved by the decision of a local board granting, denying, changing or discontinuing public assistance may appeal such decision pursuant to § <a href="http://law.lis.virginia.gov/vacode/63.2-517/" target="_blank" rel="noreferrer">63.2-517</a>. If a hearing request is received prior to the effective date of any proposed change in benefit status, a participant appealing such change shall have the right to continued direct payment of TANF benefits pending final administrative action on such appeal.</p><p>1994, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0858" target="_blank" rel="noreferrer">858</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0951" target="_blank" rel="noreferrer">951</a>, § 63.1-133.53; 1995, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0450" target="_blank" rel="noreferrer">450</a>; 2002, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0747" target="_blank" rel="noreferrer">747</a>.</p>',3),o=[n];function c(l,p,s,d,g,h){return a(),t("div",null,o)}const u=e(r,[["render",c]]);export{f as __pageData,u as default};
