import{_ as e,o as r,c as t,O as a}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 63.2-905.2 Security freezes and annual credit checks for children in foster care.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/63.2-905.2.md","filePath":"vacode/63.2-905.2.md"}'),i={name:"vacode/63.2-905.2.md"},o=a('<h1 id="§-63-2-905-2-security-freezes-and-annual-credit-checks-for-children-in-foster-care" tabindex="-1">§ 63.2-905.2 Security freezes and annual credit checks for children in foster care. <a class="header-anchor" href="#§-63-2-905-2-security-freezes-and-annual-credit-checks-for-children-in-foster-care" aria-label="Permalink to &quot;§ 63.2-905.2 Security freezes and annual credit checks for children in foster care.&quot;">​</a></h1><p>A. Local departments shall request the placement of a security freeze pursuant to the provisions of § <a href="/vacode/59.1-444.3/">59.1-444.3</a> on the credit report or record of any child who is less than 16 years of age and has been in foster care for at least six months in order to prevent cases of identity theft and misuse of personal identifying information. The local department shall request removal of the security freeze (i) upon the child&#39;s removal from foster care, (ii) upon the child&#39;s request if the child is 16 years of age or older, or (iii) upon a determination by the local department that removal of the security freeze is in the best interest of the child.</p><p>B. Local departments shall conduct annual credit checks on all children 14 years of age or older but less than 18 years of age who are in foster care to identify cases of identity theft or misuse of personal identifying information of such children. Local departments shall resolve, to the greatest extent possible, cases of identity theft or misuse of personal identifying information of foster care children identified pursuant to this section.</p><p>2012, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0432" target="_blank" rel="noreferrer">432</a>; 2016, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0631" target="_blank" rel="noreferrer">631</a>; 2019, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0676" target="_blank" rel="noreferrer">676</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0677" target="_blank" rel="noreferrer">677</a>.</p>',4),n=[o];function s(c,l,f,h,d,p){return r(),t("div",null,n)}const g=e(i,[["render",s]]);export{_ as __pageData,g as default};
