import{_ as e,o as t,c as i,O as a}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 64.2-103 Evidence of paternity.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/64.2-103.md","filePath":"vacode/64.2-103.md"}'),o={name:"vacode/64.2-103.md"},n=a('<h1 id="§-64-2-103-evidence-of-paternity" tabindex="-1">§ 64.2-103 Evidence of paternity. <a class="header-anchor" href="#§-64-2-103-evidence-of-paternity" aria-label="Permalink to &quot;§ 64.2-103 Evidence of paternity.&quot;">​</a></h1><p>A. For the purposes of this title, paternity of a child born out of wedlock shall be established by clear and convincing evidence, and such evidence may include the following:</p><ol><li><p>That he cohabited openly with the mother during all of the 10 months immediately prior to the time the child was born;</p></li><li><p>That he gave consent to a physician or other person, not including the mother, charged with the responsibility of securing information for the preparation of a birth record that his name be used as the father of the child upon the birth record of the child;</p></li><li><p>That he allowed by a general course of conduct the common use of his surname by the child;</p></li><li><p>That he claimed the child as his child on any statement, tax return, or other document filed and signed by him with any local, state, or federal government or any agency thereof;</p></li><li><p>That he admitted before any court having jurisdiction to determine his paternity that he is the father of the child;</p></li><li><p>That he voluntarily admitted paternity in writing under oath;</p></li><li><p>The results of scientifically reliable genetic tests, including DNA tests, weighted with all the evidence; or</p></li><li><p>Other medical, scientific, or anthropological evidence relating to the alleged parentage of the child based on tests performed by experts.</p></li></ol><p>B. A judgment establishing a father&#39;s paternity made by a court having jurisdiction to determine his paternity is sufficient evidence of paternity for the purposes of this section.</p><p>1978, c. 647, § 64.1-5.2; 1989, c. 466; 1991, c. 479; 1999, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0781" target="_blank" rel="noreferrer">781</a>; 2012, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0614" target="_blank" rel="noreferrer">614</a>.</p>',5),r=[n];function h(l,c,s,d,p,f){return t(),i("div",null,r)}const g=e(o,[["render",h]]);export{u as __pageData,g as default};
