import{_ as e,o as t,c as a,O as r}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 64.2-1052 Rental property.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/64.2-1052.md","filePath":"vacode/64.2-1052.md"}'),o={name:"vacode/64.2-1052.md"},i=r('<h1 id="§-64-2-1052-rental-property" tabindex="-1">§ 64.2-1052 Rental property. <a class="header-anchor" href="#§-64-2-1052-rental-property" aria-label="Permalink to &quot;§ 64.2-1052 Rental property.&quot;">​</a></h1><p>To the extent a fiduciary does not account for the management of rental property as a business under § <a href="https://law.lis.virginia.gov/vacode/title64.2/chapter10.1/section64.2-1047/" target="_blank" rel="noreferrer">64.2-1050</a>, the fiduciary shall allocate to income an amount received as rent of real or personal property, including an amount received for cancellation or renewal of a lease. An amount received as a refundable deposit, including a security deposit or a deposit that is to be applied as rent for future periods:</p><ol><li><p>Shall be added to principal and held subject to the terms of the lease, except as otherwise provided by law other than this chapter; and</p></li><li><p>Is not allocated to income or available for distribution to a beneficiary until the fiduciary&#39;s contractual obligations have been satisfied with respect to that amount.</p></li></ol><p>2022, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0354" target="_blank" rel="noreferrer">354</a>.</p>',4),n=[i];function l(s,c,p,d,h,_){return t(),a("div",null,n)}const m=e(o,[["render",l]]);export{u as __pageData,m as default};
