import{_ as e,o as a,c as t,O as o}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 64.2-1054 Insurance policy or contract.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/64.2-1054.md","filePath":"vacode/64.2-1054.md"}'),r={name:"vacode/64.2-1054.md"},i=o('<h1 id="§-64-2-1054-insurance-policy-or-contract" tabindex="-1">§ 64.2-1054 Insurance policy or contract. <a class="header-anchor" href="#§-64-2-1054-insurance-policy-or-contract" aria-label="Permalink to &quot;§ 64.2-1054 Insurance policy or contract.&quot;">​</a></h1><p>A. This section does not apply to a contract to which § <a href="https://law.lis.virginia.gov/vacode/title64.2/chapter10.1/section64.2-1056/" target="_blank" rel="noreferrer">64.2-1056</a> applies.</p><p>B. Except as otherwise provided in subsection C, a fiduciary shall allocate to principal the proceeds of a life insurance policy or other contract received by the fiduciary as beneficiary, including a contract that insures against damage to, destruction of, or loss of title to an asset. The fiduciary shall allocate dividends on an insurance policy to income to the extent premiums on the policy are paid from income and to principal to the extent premiums on the policy are paid from principal.</p><p>C. A fiduciary shall allocate to income proceeds of a contract that insures the fiduciary against loss of:</p><ol><li><p>Occupancy or other use by a current income beneficiary;</p></li><li><p>Income; or</p></li><li><p>Subject to § <a href="https://law.lis.virginia.gov/vacode/title64.2/chapter10.1/section64.2-1050/" target="_blank" rel="noreferrer">64.2-1050</a>, profits from a business.</p></li></ol><p>2022, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0354" target="_blank" rel="noreferrer">354</a>.</p>',6),c=[i];function n(s,l,p,d,h,f){return a(),t("div",null,c)}const m=e(r,[["render",n]]);export{_ as __pageData,m as default};
