import{_ as e,o as i,c as t,O as n}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 64.2-1064 Disbursement from income.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/64.2-1064.md","filePath":"vacode/64.2-1064.md"}'),r={name:"vacode/64.2-1064.md"},o=n('<h1 id="§-64-2-1064-disbursement-from-income" tabindex="-1">§ 64.2-1064 Disbursement from income. <a class="header-anchor" href="#§-64-2-1064-disbursement-from-income" aria-label="Permalink to &quot;§ 64.2-1064 Disbursement from income.&quot;">​</a></h1><p>Subject to § <a href="https://law.lis.virginia.gov/vacode/title64.2/chapter10.1/section64.2-1067/" target="_blank" rel="noreferrer">64.2-1067</a>, and except as otherwise provided in subdivision C 2 or 3 of § <a href="https://law.lis.virginia.gov/vacode/title64.2/chapter10.1/section64.2-1071/" target="_blank" rel="noreferrer">64.2-1071</a>, a fiduciary shall disburse from income:</p><ol><li>One-half of:</li></ol><p>a. The regular compensation of the fiduciary and any person providing investment advisory, custodial, or other services to the fiduciary, to the extent income is sufficient; and</p><p>b. An expense for an accounting, judicial or nonjudicial proceeding, or other matter that involves both income and successive interests, to the extent income is sufficient;</p><ol start="2"><li><p>The balance of the disbursements described in subdivision 1, to the extent a fiduciary that is an independent person determines that making those disbursements from income would be in the interests of the beneficiaries;</p></li><li><p>Another ordinary expense incurred in connection with administration, management, or preservation of property and distribution of income, including interest, an ordinary repair, regularly recurring tax assessed against principal, and an expense of an accounting, judicial or nonjudicial proceeding, or other matter that involves primarily an income interest, to the extent income is sufficient; and</p></li><li><p>A premium on insurance covering loss of a principal asset or income from or use of the asset.</p></li></ol><p>2022, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0354" target="_blank" rel="noreferrer">354</a>.</p>',7),a=[o];function s(c,d,l,p,m,f){return i(),t("div",null,a)}const _=e(r,[["render",s]]);export{u as __pageData,_ as default};
