import{_ as e,o,c as t,O as i}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 64.2-121 Disclosure of content of electronic communications of deceased user.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/64.2-121.md","filePath":"vacode/64.2-121.md"}'),n={name:"vacode/64.2-121.md"},c=i('<h1 id="§-64-2-121-disclosure-of-content-of-electronic-communications-of-deceased-user" tabindex="-1">§ 64.2-121 Disclosure of content of electronic communications of deceased user. <a class="header-anchor" href="#§-64-2-121-disclosure-of-content-of-electronic-communications-of-deceased-user" aria-label="Permalink to &quot;§ 64.2-121 Disclosure of content of electronic communications of deceased user.&quot;">​</a></h1><p>If a deceased user consented to or a court directs disclosure of the contents of electronic communications of the user, the custodian shall disclose to the personal representative of the estate of the user the content of electronic communications sent or received by the user if the representative gives the custodian:</p><ol><li><p>A written request for disclosure in physical or electronic form;</p></li><li><p>A certified copy of the death certificate of the user;</p></li><li><p>A certified copy of the letter of appointment of the representative or a small-estate affidavit or court order;</p></li><li><p>Unless the user provided direction using an online tool, a copy of the user&#39;s will, trust, power of attorney, or other record evidencing the user&#39;s consent to disclosure of the content of electronic communications; and</p></li><li><p>If requested by the custodian:</p></li></ol><p>a. A number, username, address, or other unique subscriber or account identifier assigned by the custodian to identify the user&#39;s account;</p><p>b. Evidence linking the account to the user; or</p><p>c. A finding by the court that (i) the user had a specific account with the custodian, identifiable by the information specified in subdivision a; (ii) disclosure of the content of electronic communications of the user would not violate 18 U.S.C. § 2701 et seq., 47 U.S.C. § 222, or other applicable law; (iii) unless the user provided direction using an online tool, the user consented to disclosure of the content of electronic communications; or (iv) disclosure of the content of electronic communications of the user is reasonably necessary for administration of the estate.</p><p>2017, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0033" target="_blank" rel="noreferrer">33</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0080" target="_blank" rel="noreferrer">80</a>.</p>',7),r=[c];function s(a,l,d,u,f,h){return o(),t("div",null,r)}const _=e(n,[["render",s]]);export{m as __pageData,_ as default};
