import{_ as e,o as t,c as o,O as r}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 64.2-126 Disclosure of contents of electronic communications held in trust when trustee is not original user.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/64.2-126.md","filePath":"vacode/64.2-126.md"}'),i={name:"vacode/64.2-126.md"},n=r('<h1 id="§-64-2-126-disclosure-of-contents-of-electronic-communications-held-in-trust-when-trustee-is-not-original-user" tabindex="-1">§ 64.2-126 Disclosure of contents of electronic communications held in trust when trustee is not original user. <a class="header-anchor" href="#§-64-2-126-disclosure-of-contents-of-electronic-communications-held-in-trust-when-trustee-is-not-original-user" aria-label="Permalink to &quot;§ 64.2-126 Disclosure of contents of electronic communications held in trust when trustee is not original user.&quot;">​</a></h1><p>Unless otherwise ordered by the court, directed by the user, or provided in a trust, a custodian shall disclose to a trustee that is not an original user of an account the content of electronic communications sent or received by an original or successor user and carried, maintained, processed, received, or stored by the custodian in the account of the trust if the trustee gives the custodian:</p><ol><li><p>A written request for disclosure in physical or electronic form;</p></li><li><p>A certified copy of the trust instrument, or a certification of the trust under § <a href="http://law.lis.virginia.gov/vacode/64.2-804/" target="_blank" rel="noreferrer">64.2-804</a> that includes consent to disclosure of the content of electronic communications to the trustee;</p></li><li><p>A certification by the trustee that the trust exists and the trustee is a currently acting trustee of the trust; and</p></li><li><p>If requested by the custodian:</p></li></ol><p>a. A number, username, address, or other unique subscriber or account identifier assigned by the custodian to identify the trust&#39;s account; or</p><p>b. Evidence linking the account to the trust.</p><p>2017, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0033" target="_blank" rel="noreferrer">33</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0080" target="_blank" rel="noreferrer">80</a>.</p>',6),s=[n];function c(a,u,l,d,h,f){return t(),o("div",null,s)}const m=e(i,[["render",c]]);export{_ as __pageData,m as default};
