import{_ as e,o as t,c as r,O as a}from"./chunks/framework.9482e208.js";const g=JSON.parse(`{"title":"§ 64.2-1305 Conservators, guardians of minors' estates, committees, trustees under § 64.2-2016, and receivers.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/64.2-1305.md","filePath":"vacode/64.2-1305.md"}`),i={name:"vacode/64.2-1305.md"},o=a('<h1 id="§-64-2-1305-conservators-guardians-of-minors-estates-committees-trustees-under-§-64-2-2016-and-receivers" tabindex="-1">§ 64.2-1305 Conservators, guardians of minors&#39; estates, committees, trustees under § 64.2-2016, and receivers. <a class="header-anchor" href="#§-64-2-1305-conservators-guardians-of-minors-estates-committees-trustees-under-§-64-2-2016-and-receivers" aria-label="Permalink to &quot;§ 64.2-1305 Conservators, guardians of minors&#39; estates, committees, trustees under § 64.2-2016, and receivers.&quot;">​</a></h1><p>A. Within six months from the date of the qualification, conservators, guardians of minors&#39; estates, committees, and trustees under § <a href="/vacode/64.2-2016/">64.2-2016</a> shall exhibit before the commissioner of accounts a statement of all money and other property that the fiduciary has received, has become chargeable with, or has disbursed within four months from the date of qualification.</p><p>B. After the first account of the fiduciary has been filed and settled, the second and subsequent accounts for each succeeding 12-month period shall be due within four months from the last day of the 12-month period commencing on the terminal date of the preceding account unless the commissioner of accounts extends the period for filing upon reasonable cause.</p><p>C. For fiduciaries acting on behalf of Medicaid recipients, the fees charged by the commissioners of accounts under subsection A or B shall not exceed $25.</p><p>D. Any account filed with the commissioner pursuant to this section shall be signed under oath by the fiduciary making such filing. If a fiduciary makes a false entry or statement in such a filing, he shall be subject to a civil penalty of not more than $500. Such penalty shall be collected by the attorney for the Commonwealth or the county or city attorney, and the proceeds shall be deposited into the general fund.</p><p>1993, c. 689, § 26-17.4; 1997, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0214" target="_blank" rel="noreferrer">214</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0921" target="_blank" rel="noreferrer">921</a>; 1999, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0016" target="_blank" rel="noreferrer">16</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0378" target="_blank" rel="noreferrer">378</a>; 2012, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0614" target="_blank" rel="noreferrer">614</a>; 2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0190" target="_blank" rel="noreferrer">190</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0372" target="_blank" rel="noreferrer">372</a>.</p>',6),s=[o];function n(c,l,h,d,f,u){return t(),r("div",null,s)}const p=e(i,[["render",n]]);export{g as __pageData,p as default};
