import{_ as e,o as r,c as a,O as t}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 64.2-1308 Forms for inventories and accounts.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/64.2-1308.md","filePath":"vacode/64.2-1308.md"}'),o={name:"vacode/64.2-1308.md"},i=t('<h1 id="§-64-2-1308-forms-for-inventories-and-accounts" tabindex="-1">§ 64.2-1308 Forms for inventories and accounts. <a class="header-anchor" href="#§-64-2-1308-forms-for-inventories-and-accounts" aria-label="Permalink to &quot;§ 64.2-1308 Forms for inventories and accounts.&quot;">​</a></h1><p>The Office of the Executive Secretary of the Supreme Court shall provide to each circuit court clerk forms and instructions for the inventories required by § <a href="http://law.lis.virginia.gov/vacode/64.2-1300/" target="_blank" rel="noreferrer">64.2-1300</a> and forms and instructions for accounts. The clerk shall provide the appropriate forms to every fiduciary who qualifies in the clerk&#39;s office. An inventory filed pursuant to § <a href="http://law.lis.virginia.gov/vacode/64.2-1300/" target="_blank" rel="noreferrer">64.2-1300</a> or an account filed pursuant to § <a href="http://law.lis.virginia.gov/vacode/64.2-1206/" target="_blank" rel="noreferrer">64.2-1206</a> may be made on the form provided to the fiduciary by the clerk of the court, on a computer-generated facsimile of the appropriate form, or in any other clear format.</p><p>1966, c. 336, §§ 26-12.1, 26-17.3; 1972, c. 411; 1993, cc. 581, 689, § 26-17.3; 1997, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0842" target="_blank" rel="noreferrer">842</a>; 2012, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0614" target="_blank" rel="noreferrer">614</a>.</p>',3),n=[i];function c(s,f,l,d,h,p){return r(),a("div",null,n)}const v=e(o,[["render",c]]);export{u as __pageData,v as default};
