import{_ as e,o,c as r,O as i}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 64.2-1409 Information to be provided to clerk by fiduciary.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/64.2-1409.md","filePath":"vacode/64.2-1409.md"}'),t={name:"vacode/64.2-1409.md"},a=i('<h1 id="§-64-2-1409-information-to-be-provided-to-clerk-by-fiduciary" tabindex="-1">§ 64.2-1409 Information to be provided to clerk by fiduciary. <a class="header-anchor" href="#§-64-2-1409-information-to-be-provided-to-clerk-by-fiduciary" aria-label="Permalink to &quot;§ 64.2-1409 Information to be provided to clerk by fiduciary.&quot;">​</a></h1><p>A. On and after July 1, 1998, every person seeking to qualify in any fiduciary capacity before the circuit court or clerk shall provide to the court or clerk the information required to make the qualification on forms provided to the proposed fiduciary by the clerk. The forms, with appropriate instructions concerning their use, shall be provided to each clerk by the Office of the Executive Secretary of the Supreme Court. In lieu of any form, a computer-generated facsimile of the form may be used by any person seeking to qualify.</p><p>B. Every qualified fiduciary who moves from the Commonwealth and becomes resident in another state shall inform the clerk and the commissioner of accounts of the court in which he was qualified of his new address within 30 days of the date of the change in residency. Any fiduciary who fails to so inform the clerk and commissioner of accounts shall be subject to a civil penalty of $50. For purposes of this section, a person becomes resident in another state when he can no longer satisfy the residency requirements specified in § <a href="http://law.lis.virginia.gov/vacode/38.2-1800.1/" target="_blank" rel="noreferrer">38.2-1800.1</a>. This section shall not apply to any fiduciary whose cofiduciary is a resident of the Commonwealth.</p><p>1997, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0842" target="_blank" rel="noreferrer">842</a>, § 26-1.2; 2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0644" target="_blank" rel="noreferrer">644</a>; 2012, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0614" target="_blank" rel="noreferrer">614</a>.</p>',4),n=[a];function c(s,f,l,d,h,p){return o(),r("div",null,n)}const y=e(t,[["render",c]]);export{m as __pageData,y as default};
