import{_ as e,o as r,c as t,O as o}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 64.2-1420 Clerk to mail notice, process, or rule to person served.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/64.2-1420.md","filePath":"vacode/64.2-1420.md"}'),a={name:"vacode/64.2-1420.md"},s=o('<h1 id="§-64-2-1420-clerk-to-mail-notice-process-or-rule-to-person-served" tabindex="-1">§ 64.2-1420 Clerk to mail notice, process, or rule to person served. <a class="header-anchor" href="#§-64-2-1420-clerk-to-mail-notice-process-or-rule-to-person-served" aria-label="Permalink to &quot;§ 64.2-1420 Clerk to mail notice, process, or rule to person served.&quot;">​</a></h1><p>Whenever any notice, process, or rule is served on the clerk of a circuit court pursuant to § <a href="http://law.lis.virginia.gov/vacode/64.2-1419/" target="_blank" rel="noreferrer">64.2-1419</a>, the clerk shall mail the notice, process, or rule forthwith by certified or registered mail, postage prepaid, to the person thus served, to his last known address as shown by the court papers, the cost thereof to be paid in advance by the person desiring the service. In lieu of using certified or registered mail, the clerk of court may also use overnight delivery, with the cost thereof to be paid in advance by the person desiring service.</p><p>1954, c. 601, § 26-7.2; 2004, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0367" target="_blank" rel="noreferrer">367</a>; 2012, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0614" target="_blank" rel="noreferrer">614</a>.</p>',3),i=[s];function n(c,l,p,d,h,_){return r(),t("div",null,i)}const f=e(a,[["render",n]]);export{v as __pageData,f as default};
