import{_ as o,o as r,c as a,z as e,a as t}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 64.2-1427 How property of nonresident infant or incapacitated person transferred to foreign guardian, conservator, or committee.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/64.2-1427.md","filePath":"vacode/64.2-1427.md"}'),n={name:"vacode/64.2-1427.md"},i=e("h1",{id:"§-64-2-1427-how-property-of-nonresident-infant-or-incapacitated-person-transferred-to-foreign-guardian-conservator-or-committee",tabindex:"-1"},[t("§ 64.2-1427 How property of nonresident infant or incapacitated person transferred to foreign guardian, conservator, or committee. "),e("a",{class:"header-anchor",href:"#§-64-2-1427-how-property-of-nonresident-infant-or-incapacitated-person-transferred-to-foreign-guardian-conservator-or-committee","aria-label":'Permalink to "§ 64.2-1427 How property of nonresident infant or incapacitated person transferred to foreign guardian, conservator, or committee."'},"​")],-1),s=e("p",null,"When any nonresident infant or incapacitated person is entitled to property or money in the Commonwealth, a petition to remove the property or money to the domicile of the infant or incapacitated person may be filed by his guardian, conservator, committee, or other fiduciary lawfully appointed and qualified in the state or country of his residence, in the circuit court of the county or city in which the property or money, or some part thereof, is located. If entitlement to the property or money was acquired other than by a will or was acquired by a will that restricts the transfer out of the Commonwealth, the infant or incapacitated person, and the guardian of the infant or the conservator or other fiduciary of the incapacitated person appointed in the Commonwealth, if there is one, shall be made a party defendant to this petition. The court shall appoint a guardian ad litem for the infant or incapacitated person who, as well as the conservator or other fiduciary, if there is one, shall answer the petition on oath. Upon a hearing of the case on its merits, or upon the petition without hearing if entitlement to the property or money was acquired by a will that does not restrict the transfer out of the Commonwealth, the court may order the fiduciary to pay and deliver to the foreign guardian, conservator, committee, or fiduciary, or his agent or attorney, all personal property and money in his possession belonging to the infant or incapacitated person, and authorize the foreign guardian, conservator, committee, or fiduciary to sue for, recover, and receive all money and personal property, including the accruing rents of his real estate, that belongs to the infant or incapacitated person in the same manner as if he were appointed a guardian, conservator, committee, or fiduciary of the infant or incapacitated person in the Commonwealth, and to remove the money and personal property to the state or country in which the foreign fiduciary was appointed and qualified.",-1),c=e("p",null,[t("Code 1919, § 5350; Code 1950, § 26-60; 1968, c. 399; 1983, c. 487; 1997, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0801",target:"_blank",rel:"noreferrer"},"801"),t("; 2012, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0614",target:"_blank",rel:"noreferrer"},"614"),t(".")],-1),d=[i,s,c];function h(p,f,l,m,u,y){return r(),a("div",null,d)}const w=o(n,[["render",h]]);export{v as __pageData,w as default};
