import{_ as e,o as t,c as a,O as n}from"./chunks/framework.9482e208.js";const b=JSON.parse('{"title":"§ 64.2-1430 When bond may be dispensed with.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/64.2-1430.md","filePath":"vacode/64.2-1430.md"}'),i={name:"vacode/64.2-1430.md"},o=n('<h1 id="§-64-2-1430-when-bond-may-be-dispensed-with" tabindex="-1">§ 64.2-1430 When bond may be dispensed with. <a class="header-anchor" href="#§-64-2-1430-when-bond-may-be-dispensed-with" aria-label="Permalink to &quot;§ 64.2-1430 When bond may be dispensed with.&quot;">​</a></h1><p>In any case in which the circuit court finds that the laws of the state or country in which the infant or incapacitated person resides and the foreign guardian, conservator, or committee was appointed and qualified do not provide for the giving of a bond by the guardian, conservator, or committee, the court, in its discretion, may permit the money and other estate of the infant or incapacitated person to be paid and delivered to the foreign fiduciary although he has not given the bond required by § <a href="http://law.lis.virginia.gov/vacode/64.2-1429/" target="_blank" rel="noreferrer">64.2-1429</a>.</p><p>Code 1919, § 5352; 1930, p. 736; Code 1950, § 26-63; 1997, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0801" target="_blank" rel="noreferrer">801</a>; 2012, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0614" target="_blank" rel="noreferrer">614</a>.</p>',3),r=[o];function d(s,h,c,p,_,l){return t(),a("div",null,r)}const g=e(i,[["render",d]]);export{b as __pageData,g as default};
