import{_ as e,o as t,c as o,O as r}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 64.2-1603 Execution of power of attorney.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/64.2-1603.md","filePath":"vacode/64.2-1603.md"}'),a={name:"vacode/64.2-1603.md"},n=r('<h1 id="§-64-2-1603-execution-of-power-of-attorney" tabindex="-1">§ 64.2-1603 Execution of power of attorney. <a class="header-anchor" href="#§-64-2-1603-execution-of-power-of-attorney" aria-label="Permalink to &quot;§ 64.2-1603 Execution of power of attorney.&quot;">​</a></h1><p>A power of attorney shall be signed by the principal or in the principal&#39;s conscious presence by another individual directed by the principal to sign the principal&#39;s name on the power of attorney. A signature on a power of attorney is presumed to be genuine if the principal acknowledges the signature before a notary public or other individual authorized by law to take acknowledgments. A power of attorney in order to be recordable shall satisfy the requirements of § <a href="/vacode/55.1-600/">55.1-600</a>.</p><p>2010, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0455" target="_blank" rel="noreferrer">455</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0632" target="_blank" rel="noreferrer">632</a>, § 26-76; 2012, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0614" target="_blank" rel="noreferrer">614</a>.</p>',3),i=[n];function c(l,s,p,f,d,_){return t(),o("div",null,i)}const g=e(a,[["render",c]]);export{u as __pageData,g as default};
