import{_ as e,o as t,c as o,O as a}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 64.2-1642 Effect on existing powers of attorney.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/64.2-1642.md","filePath":"vacode/64.2-1642.md"}'),i={name:"vacode/64.2-1642.md"},r=a('<h1 id="§-64-2-1642-effect-on-existing-powers-of-attorney" tabindex="-1">§ 64.2-1642 Effect on existing powers of attorney. <a class="header-anchor" href="#§-64-2-1642-effect-on-existing-powers-of-attorney" aria-label="Permalink to &quot;§ 64.2-1642 Effect on existing powers of attorney.&quot;">​</a></h1><p>Except as otherwise provided in this chapter, on July 1, 2010:</p><ol><li><p>This chapter applies to a power of attorney created before, on, or after July 1, 2010;</p></li><li><p>This chapter applies to a judicial proceeding concerning a power of attorney commenced on or after July 1, 2010;</p></li><li><p>This chapter applies to a judicial proceeding concerning a power of attorney commenced before July 1, 2010, unless the court finds that application of a provision of this chapter would substantially interfere with the effective conduct of the judicial proceeding or prejudice the rights of a party, in which case that provision does not apply and the superseded law applies; and</p></li><li><p>Notwithstanding any other provision of this chapter, an act done before July 1, 2010, is not affected by this chapter.</p></li></ol><p>2010, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0455" target="_blank" rel="noreferrer">455</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0632" target="_blank" rel="noreferrer">632</a>, § 26-116; 2012, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0614" target="_blank" rel="noreferrer">614</a>.</p>',4),n=[r];function c(p,s,l,f,h,d){return t(),o("div",null,n)}const u=e(i,[["render",c]]);export{g as __pageData,u as default};
