import{_ as e,o as a,c as t,O as s}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 64.2-202 When persons take per capita and when per stirpes; collaterals of the half blood.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/64.2-202.md","filePath":"vacode/64.2-202.md"}'),o={name:"vacode/64.2-202.md"},r=s('<h1 id="§-64-2-202-when-persons-take-per-capita-and-when-per-stirpes-collaterals-of-the-half-blood" tabindex="-1">§ 64.2-202 When persons take per capita and when per stirpes; collaterals of the half blood. <a class="header-anchor" href="#§-64-2-202-when-persons-take-per-capita-and-when-per-stirpes-collaterals-of-the-half-blood" aria-label="Permalink to &quot;§ 64.2-202 When persons take per capita and when per stirpes; collaterals of the half blood.&quot;">​</a></h1><p>A. A decedent&#39;s estate, or each half portion of such estate when division is required by subdivision A 5 of § <a href="http://law.lis.virginia.gov/vacode/64.2-200/" target="_blank" rel="noreferrer">64.2-200</a>, shall, except when otherwise provided in subdivision A 1 of § <a href="http://law.lis.virginia.gov/vacode/64.2-200/" target="_blank" rel="noreferrer">64.2-200</a>, be divided into as many equal shares as there are (i) heirs and distributees who are in the closest degree of kinship to the decedent and (ii) deceased persons, if any, in the same degree of kinship to the decedent who, if living, would have been heirs and distributees and who left descendants surviving at the time of the decedent&#39;s death. One share of the estate or half portion thereof shall descend and pass to each such heir and distributee and one share shall descend and pass per stirpes to such descendants.</p><p>B. Notwithstanding the provisions of subsection A, collaterals of the half blood shall inherit only half as much as those of the whole blood.</p><p>Code 1950, §§ 64-2, 64-3; 1968, c. 656, §§ 64.1-2, 64.1-3; 1986, c. 305; 2012, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0614" target="_blank" rel="noreferrer">614</a>.</p>',4),n=[r];function i(h,d,l,c,p,f){return a(),t("div",null,n)}const b=e(o,[["render",i]]);export{v as __pageData,b as default};
