import{_ as e,o as t,c as a,O as r}from"./chunks/framework.9482e208.js";const g=JSON.parse(`{"title":"§ 64.2-2026 Surrender of incapacitated person's estate.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/64.2-2026.md","filePath":"vacode/64.2-2026.md"}`),i={name:"vacode/64.2-2026.md"},n=r('<h1 id="§-64-2-2026-surrender-of-incapacitated-person-s-estate" tabindex="-1">§ 64.2-2026 Surrender of incapacitated person&#39;s estate. <a class="header-anchor" href="#§-64-2-2026-surrender-of-incapacitated-person-s-estate" aria-label="Permalink to &quot;§ 64.2-2026 Surrender of incapacitated person&#39;s estate.&quot;">​</a></h1><p>A. If the incapacitated person is restored to capacity, the fiduciary shall surrender the incapacitated person&#39;s estate or that portion for which he is accountable to the incapacitated person.</p><p>B. If the incapacitated person dies prior to being restored to capacity, the fiduciary shall surrender the real estate to the incapacitated person&#39;s heirs or devisees and the personal estate to his executors or administrators. If, at the time of the death of the incapacitated person, (i) the value of the personal estate in the custody of the fiduciary is $25,000 or less, (ii) a personal representative has not qualified within 60 days of the incapacitated person&#39;s death, and (iii) the fiduciary does not anticipate that anyone will qualify, the fiduciary may pay the balance of the incapacitated person&#39;s estate to the incapacitated person&#39;s surviving spouse or, if there is no surviving spouse, to the distributees of the incapacitated person or other persons entitled thereto, including any person or entity entitled to payment for funeral or burial services provided. The distribution shall be noted in the fiduciary&#39;s final accounting submitted to the commissioner of accounts.</p><p>Code 1950, § 37-151; 1968, c. 477, § 37.1-144; 1995, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0344" target="_blank" rel="noreferrer">344</a>; 1997, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0921" target="_blank" rel="noreferrer">921</a>; 2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0195" target="_blank" rel="noreferrer">195</a>; 2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0716" target="_blank" rel="noreferrer">716</a>, § 37.2-1027; 2012, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0614" target="_blank" rel="noreferrer">614</a>; 2014, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0532" target="_blank" rel="noreferrer">532</a>.</p>',4),o=[n];function s(c,p,d,h,l,f){return t(),a("div",null,o)}const _=e(i,[["render",s]]);export{g as __pageData,_ as default};
