import{_ as e,o as t,c as o,O as i}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 64.2-2107 Jurisdiction.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/64.2-2107.md","filePath":"vacode/64.2-2107.md"}'),n={name:"vacode/64.2-2107.md"},a=i('<h1 id="§-64-2-2107-jurisdiction" tabindex="-1">§ 64.2-2107 Jurisdiction. <a class="header-anchor" href="#§-64-2-2107-jurisdiction" aria-label="Permalink to &quot;§ 64.2-2107 Jurisdiction.&quot;">​</a></h1><p>A court of the Commonwealth has jurisdiction to appoint a guardian or issue a conservatorship order for a respondent if:</p><ol><li><p>The Commonwealth is the respondent&#39;s home state;</p></li><li><p>On the date the petition is filed, the Commonwealth is a significant-connection state and:</p></li></ol><p>a. The respondent does not have a home state or a court of the respondent&#39;s home state has declined to exercise jurisdiction because the Commonwealth is a more appropriate forum; or</p><p>b. The respondent has a home state, a petition for an appointment or order is not pending in a court of that state or another significant-connection state, and, before the court makes the appointment or issues the order:</p><p>(1) A petition for an appointment or order is not filed in the respondent&#39;s home state;</p><p>(2) An objection to the court&#39;s jurisdiction is not filed by a person required to be notified of the proceeding; and</p><p>(3) The court in the Commonwealth concludes that it is an appropriate forum under the factors set forth in § <a href="http://law.lis.virginia.gov/vacode/64.2-2110/" target="_blank" rel="noreferrer">64.2-2110</a>;</p><ol start="3"><li><p>The Commonwealth does not have jurisdiction under either subdivision 1 or 2, the respondent&#39;s home state and all significant-connection states have declined to exercise jurisdiction because the Commonwealth is the more appropriate forum, and jurisdiction in the Commonwealth is consistent with the Constitutions of Virginia and the United States; or</p></li><li><p>The requirements for special jurisdiction under § <a href="http://law.lis.virginia.gov/vacode/64.2-2108/" target="_blank" rel="noreferrer">64.2-2108</a> are met.</p></li></ol><p>2011, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0518" target="_blank" rel="noreferrer">518</a>, § 37.2-1039; 2012, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0614" target="_blank" rel="noreferrer">614</a>.</p>',10),r=[a];function s(h,c,d,p,l,m){return t(),o("div",null,r)}const _=e(n,[["render",s]]);export{f as __pageData,_ as default};
