import{_ as e,o as t,c as a,O as r}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 64.2-2500 Definitions.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/64.2-2500.md","filePath":"vacode/64.2-2500.md"}'),o={name:"vacode/64.2-2500.md"},n=r('<h1 id="§-64-2-2500-definitions" tabindex="-1">§ 64.2-2500 Definitions. <a class="header-anchor" href="#§-64-2-2500-definitions" aria-label="Permalink to &quot;§ 64.2-2500 Definitions.&quot;">​</a></h1><p>As used in this chapter:</p><p>&quot;Decedent&quot; means any person whose life has been taken as a result of murder or voluntary manslaughter.</p><p>&quot;Property&quot; includes any real and personal property and any right or interest therein.</p><p>&quot;Slayer&quot; means any person (i) who is convicted of the murder or voluntary manslaughter of the decedent or, (ii) in the absence of such conviction, who is determined, whether before or after his death, by a court of appropriate jurisdiction by a preponderance of the evidence to have committed one of the offenses listed in clause (i) resulting in the death of the decedent. For the purposes of clause (ii), the party seeking to establish that a decedent was slain by such person shall have the burden of proof.</p><p>1981, c. 469, § 55-401; 1987, c. 498; 2008, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0822" target="_blank" rel="noreferrer">822</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0830" target="_blank" rel="noreferrer">830</a>; 2012, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0614" target="_blank" rel="noreferrer">614</a>.</p>',6),i=[n];function s(c,h,l,d,p,f){return t(),a("div",null,i)}const g=e(o,[["render",s]]);export{_ as __pageData,g as default};
