import{_ as e,o as t,c as r,O as a}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 64.2-2509 Persons acquiring from slayer protected.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/64.2-2509.md","filePath":"vacode/64.2-2509.md"}'),o={name:"vacode/64.2-2509.md"},i=a('<h1 id="§-64-2-2509-persons-acquiring-from-slayer-protected" tabindex="-1">§ 64.2-2509 Persons acquiring from slayer protected. <a class="header-anchor" href="#§-64-2-2509-persons-acquiring-from-slayer-protected" aria-label="Permalink to &quot;§ 64.2-2509 Persons acquiring from slayer protected.&quot;">​</a></h1><p>The provisions of this chapter shall not affect the right of any person who, before the interests of the slayer have been adjudicated, acquires from the slayer for adequate consideration property or an interest therein that the slayer would have received except for the terms of this chapter, provided that such property or interest is acquired without notice of circumstances tending to bring it within the provisions of this chapter. All consideration received by the slayer shall be held by him in trust for the persons entitled to the property under the provisions of this chapter, and the slayer shall be liable for any portion of such consideration that he may have dissipated and for any difference between the actual value of the property and the amount of such consideration.</p><p>1981, c. 469, § 55-412; 2008, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0822" target="_blank" rel="noreferrer">822</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0830" target="_blank" rel="noreferrer">830</a>; 2012, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0614" target="_blank" rel="noreferrer">614</a>.</p>',3),n=[i];function s(c,h,l,p,d,f){return t(),r("div",null,n)}const g=e(o,[["render",s]]);export{u as __pageData,g as default};
