import{_ as e,o as t,c as a,O as r}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 64.2-2607 Disclaimer of power of appointment or other power not held in a fiduciary capacity.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/64.2-2607.md","filePath":"vacode/64.2-2607.md"}'),o={name:"vacode/64.2-2607.md"},i=r('<h1 id="§-64-2-2607-disclaimer-of-power-of-appointment-or-other-power-not-held-in-a-fiduciary-capacity" tabindex="-1">§ 64.2-2607 Disclaimer of power of appointment or other power not held in a fiduciary capacity. <a class="header-anchor" href="#§-64-2-2607-disclaimer-of-power-of-appointment-or-other-power-not-held-in-a-fiduciary-capacity" aria-label="Permalink to &quot;§ 64.2-2607 Disclaimer of power of appointment or other power not held in a fiduciary capacity.&quot;">​</a></h1><p>If a holder disclaims a power of appointment or other power not held in a fiduciary capacity, the following rules apply:</p><ol><li><p>If the holder has not exercised the power, the disclaimer takes effect as of the time the instrument creating the power becomes irrevocable.</p></li><li><p>If the holder has exercised the power and the disclaimer is of a power other than a presently exercisable general power of appointment, the disclaimer takes effect immediately after the last exercise of the power.</p></li><li><p>The instrument creating the power is construed as if the power expired when the disclaimer became effective.</p></li></ol><p>2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0253" target="_blank" rel="noreferrer">253</a>, § 64.1-196.8; 2012, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0614" target="_blank" rel="noreferrer">614</a>.</p>',4),c=[i];function n(p,l,s,h,f,d){return t(),a("div",null,c)}const w=e(o,[["render",n]]);export{_ as __pageData,w as default};
