import{_ as e,o,c as t,O as a}from"./chunks/framework.9482e208.js";const h=JSON.parse('{"title":"§ 64.2-2707 Creation of power of appointment.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/64.2-2707.md","filePath":"vacode/64.2-2707.md"}'),n={name:"vacode/64.2-2707.md"},p=a('<h1 id="§-64-2-2707-creation-of-power-of-appointment" tabindex="-1">§ 64.2-2707 Creation of power of appointment. <a class="header-anchor" href="#§-64-2-2707-creation-of-power-of-appointment" aria-label="Permalink to &quot;§ 64.2-2707 Creation of power of appointment.&quot;">​</a></h1><p>A. A power of appointment is created only if:</p><ol><li>The instrument creating the power:</li></ol><p>a. Is valid under applicable law; and</p><p>b. Except as otherwise provided in subsection B, transfers the appointive property; and</p><ol start="2"><li>The terms of the instrument creating the power manifest the donor&#39;s intent to create in a powerholder a power of appointment over the appointive property exercisable in favor of a permissible appointee.</li></ol><p>B. Subdivision A 1 b does not apply to the creation of a power of appointment by the exercise of a power of appointment.</p><p>C. A power of appointment may not be created in a deceased individual.</p><p>D. Subject to an applicable rule against perpetuities, a power of appointment may be created in an unborn or unascertained powerholder.</p><p>2016, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0266" target="_blank" rel="noreferrer">266</a>.</p>',10),r=[p];function i(s,c,l,d,f,_){return o(),t("div",null,r)}const u=e(n,[["render",i]]);export{h as __pageData,u as default};
