import{_ as e,o,c as t,O as n}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 64.2-2717 Permissible appointment.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/64.2-2717.md","filePath":"vacode/64.2-2717.md"}'),a={name:"vacode/64.2-2717.md"},r=n('<h1 id="§-64-2-2717-permissible-appointment" tabindex="-1">§ 64.2-2717 Permissible appointment. <a class="header-anchor" href="#§-64-2-2717-permissible-appointment" aria-label="Permalink to &quot;§ 64.2-2717 Permissible appointment.&quot;">​</a></h1><p>A. A powerholder of a general power of appointment that permits appointment to the powerholder or the powerholder&#39;s estate may make any appointment, including an appointment in trust or creating a new power of appointment, that the powerholder could make in disposing of the powerholder&#39;s own property.</p><p>B. A powerholder of a general power of appointment that permits appointment only to the creditors of the powerholder or of the powerholder&#39;s estate may appoint only to those creditors.</p><p>C. Unless the terms of the instrument creating a power of appointment manifest a contrary intent, the powerholder of a nongeneral power may:</p><ol><li><p>Make an appointment in any form, including an appointment in trust, in favor of a permissible appointee;</p></li><li><p>Create a general power or a nongeneral power in a permissible appointee; or</p></li><li><p>Create a nongeneral power in an impermissible appointee to appoint to one or more of the permissible appointees of the original nongeneral power.</p></li></ol><p>2016, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0266" target="_blank" rel="noreferrer">266</a>.</p>',6),p=[r];function i(s,l,m,h,c,d){return o(),t("div",null,p)}const w=e(a,[["render",i]]);export{f as __pageData,w as default};
